object(text,'1.4.3.3','THE ULURU FLAG',[
id-128,
typeOfObject-'TEXT',
text_id-128,
txt-'1.4.3.3',
text_type-'',
text_category-'',
text_title_en-'THE ULURU FLAG',
text_title_ge-'',
text_keywords-'',
text_date-'1986-00-00',
text_summary_en-'',
text_summary_ge-'',
text_language-'en',
text_contents-'&lt;font size=&quot;3&quot;&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Proposal for a flag for &lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Australia&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt; &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/span&gt;&lt;/font&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;worthy of a continent-nation &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;symbolizing its position &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;in the southern hemisphere. &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;The flag shows unmistakably &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;terra australis - the southern land. &lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;',
text_contents_en-'&lt;font size=&quot;3&quot;&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Proposal for a flag for &lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Australia&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/span&gt;&lt;/font&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;worthy of a continent-nation&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;symbolizing its position&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;in the southern hemisphere.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;The flag shows unmistakably&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;terra australis - the southern land.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;It is the island continent down under&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;the enormous red land received from above.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt; &lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;The flag holds Ayers Rock in the centre&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;font size=&quot;3&quot;&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;this central sacred symbol of &lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Australia&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/span&gt;&lt;/font&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;the heritage of the nation&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;created at the beginning of time.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;Uluru, Ayers Rock, flies over all Australians.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;Beneath it the aboriginal people&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;can feel again at home in their own land.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt; &lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;The seven pointed federal star in the centre&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;under the curve of the southern continent&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;symbolizes the seven territories of the nation and&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;font size=&quot;3&quot;&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;represents the political reality of modern &lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Australia&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/span&gt;&lt;/font&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;It&#039;s size and shape in the southern sky&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;are the same as on the commonwealth flag&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;thus affirming continuity of traditions.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt; &lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;This flag represents&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;man&#039;s new consciousness of space.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;his advanced vision&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;of his real position in the universe&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;and his regained responsibility in the space age&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;lifts man up into the higher dimension of this new era.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;The view from above, the view from and to the stars&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;replace the obsolete and distorted perspective vision.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;There is no up and down in space.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;Man must be aware&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;That he is not the centre of the universe.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt; &lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;font size=&quot;3&quot;&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;The big curve - the bend of &lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;Australia&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&#039;s vastness&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/span&gt;&lt;/font&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;on the surface of the earth&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;takes up the whole length of the flag.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;This curve of the horizon&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;descending from the southern sky&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;and ascending again to this sky&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;is the flag&#039;s prominent and proud symbol.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;It is unique, simple, self-evident,&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;and cannot be confused with any other flag.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;./.&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 12pt; font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;br /&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt; &lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot; /&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot; /&gt;&lt;/font&gt;&lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot; /&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;p&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot; /&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/p&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;The image of this enormous continent&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/span&gt;&lt;span style=&quot;font-family: &#039;Times New Roman&#039;&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;&lt;font size=&quot;3&quot;&gt;given to its people as a share of the earth&#039;s globe&lt;p&gt;&nbsp;&lt;/p&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font&gt;&lt;/font