object(paint,'138','CAMIONE DAVANTI A CASE',[
id-59,
typeOfObject-'paint',
workNumber-138,
title-'CAMIONE DAVANTI A CASE',
titleGerman-'TR&Atilde;UMENDER LASTWAGENFAHRER MIT SEINEN H&Atilde;USERN',
