id-513,
typeOfObject-'paint',
workNumber-606,
title-'HANA NO HANA - DIE NASENBLUME - KONRAD BAYERS TOD',
titleGerman-'HANA NO HANA - DIE NASENBLUME - KONRAD BAYERS TOD',
titleEnglish-'Hana No Hana - The Nose-Flower - Konrad Bayer&#039;s Death',
finishedPlace-'Venice',
