&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ein Vorschlag f&uuml;reine Fahne f&uuml;r Australien&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;einerkontinentumspannenden Nation w&uuml;rdig&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ein Symbol f&uuml;r ihreLage&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;in der s&uuml;dlichenHemisph&auml;re&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Fahne zeigtunverwechselbar&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Terra Australis &ndash;das s&uuml;dliche Land.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist der Inselkontinent&bdquo;da unten&ldquo; (down under)&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das riesige rote Land, vonoben empfangen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Fahne zeigt den AyersRock in der Mitte,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das zentral gelegene,heilige Symbol Australiens,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das Erbe derNation&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;entstanden am Anfang derZeit.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Uluru, der Ayers Rock,weht &uuml;ber allen Australiern.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Unter ihm k&ouml;nnen sichdie Aborigines&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wieder im eigenen Land zuHause f&uuml;hlen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der siebenzackigeBundesstern in der Mitte&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;unter der W&ouml;lbung dess&uuml;dlichen Kontinents&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;symbolisiert die siebenTerritorien des Landes und&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;steht f&uuml;r diepolitische Realit&auml;t des modernen Australien.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Seine Gr&ouml;&szlig;e undGestalt im s&uuml;dlichen Himmel&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ist dieselbe wie auf derFahne des Commonwealth&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und best&auml;tigt so dieKontinuit&auml;t der Traditionen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese Fahnestellt&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das neue Raumbewusstseindes Menschen dar,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;seine neu erworbeneEinsicht&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&uuml;ber seine wahreStellung im Universum&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und seine wiedererlangteVerantwortung im Zeitalter der Raumfahrt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sie hebt den Menschen indie h&ouml;here Dimension dieser neuen &Auml;ra.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Sicht von oben, dieSicht von und zu den Sternen&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ersetzt die &uuml;berholteund verzerrte perspektivische Sichtweise.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es gibt kein Oben undUnten im Weltraum&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch muss sichbewusst sein,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;dass er nicht derMittelpunkt des Universums ist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die weite W&ouml;lbung -die W&ouml;lbung von Australiens Weite&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;auf der Oberfl&auml;cheder Erde&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;nimmt die ganze L&auml;ngeder Fahne ein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese W&ouml;lbung desHorizonts,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;der vom s&uuml;dlichenHimmel herabsteigt&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und wieder zu diesemHimmel aufsteigt,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ist das herausragende undstolze Merkmal der Fahne.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sie ist einzigartig,einfach und selbstverst&auml;ndlich&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und kann mit keineranderen Fahne verwechselt werden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Bild dieses riesigenKontinents,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;der seinen Menschen alsderen Anteil am Erdball gegeben wurde,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wird die australischeNation vereinen in der Verpflichtung&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;zu einer ebenso enormen,geteilten Verantwortung,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;als Teil der ganzenMenschheit&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die Erde zu lieben und zubesch&uuml;tzen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die weite W&ouml;lbung derFahne - der Uluru&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;symbolisiert die Liebealler Australier&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;zu ihrem weiten,einzigartigen und gro&szlig;artigen Land,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;in Harmonie mit denTraditionen Australiens.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Inselkontinent&lt;em&gt;Down Under,&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&uuml;ber dems&uuml;dlichen Himmel gelegen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die gro&szlig;eW&ouml;lbung,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Uluru, der rote AyersRock,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und die enorme gemeinsameVerpflichtung&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;zur Erde und zurMenschlichkeit:&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist AustraliensIdentit&auml;tssymbol.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist Australiens eigeneFahne.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Uluru-Fahne kann aufzweierlei Art aufgezogen werden,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das Obere zuunterst oderdas Untere zuoberst, f&uuml;r die,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die f&auml;lschlicherweise&lt;em&gt;Down under&lt;/em&gt; als Beleidigung auffassen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das s&uuml;dliche Kreuzsteht nicht&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;allein f&uuml;r dieIdentit&auml;t Australiens,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;es ist das Sternbild dergesamten s&uuml;dlichen Hemisph&auml;re,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;einschlie&szlig;lichChile, Argentinien, Brasilien, S&uuml;dafrika,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Bolivien, Paraguay, Peru,Angola, Namibia, Madagaskar,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Mozambique, Zambia,Rhodesien, Botswana, der Seychellen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Fiji, Samoa, Tonga, Tahitiund Neuseeland.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Australiens Fahne musseigenst&auml;ndig sein&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und grundlegend anders alsdie anderer Nationen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die australische Fahnekann nicht blo&szlig; das Ergebnis&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;eines sterilen,kaltbl&uuml;tigen Grafik-Designs sein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der australischen Fahnem&uuml;ssen&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die Identit&auml;t, dasVertrauen, das Leben und die Liebe&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;der ganzen Nation und allihrer Bewohner&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;immanent sein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die australische Fahnemuss die Botschaft&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;dieses riesigen Kontinentsin eine h&ouml;here Dimension tragen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das gro&szlig;e SchicksalAustraliens widerspiegeln&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und die BedeutungAustraliens&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;als Weltmachtvoraussehen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist Australien, das dieErde festh&auml;lt .&lt;/span&gt;&lt;/p&gt;').
text_contents_ge(129,'&lt;style type=&quot;text/css&quot;&gt;

&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;Die Fahne des unabh&auml;ngigen Ost-Timor istein&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Symbol der Hoffnung, der Sch&ouml;nheit, desGl&uuml;cks.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Sie ist aus dem Schrecken derUnterdr&uuml;ckung&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und des Totschlags hervorgegangen&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;als sichtbares Zeichen des Stolzes&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und der Entwicklung einer leuchtenden&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und pr&auml;chtigen Zukunft entgegen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die fliegenden Farben auf der rechtenH&auml;lfte der Fahne&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;zeigen die Lage der Nation im &ouml;stlichenTeil der Insel Timor an.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die vier waagerechten Streifen &ndash; rot,orange, gelb und gr&uuml;n &ndash;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;sind Teil eines Regenbogens, der f&uuml;r dieHarmonie steht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Der rote Streifen symbolisiert das heroischeWerden der Nation&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;durch vergossenes Blut und den heldenhaftenKampf&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und die Opferbereitschaft des Volkes vonOst-Timor.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die anderen Streifen stehen f&uuml;r denallm&auml;hlichen Aufstieg zur Hoffnung, zum Gl&uuml;ck,&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;durch das ganz oben stehende Gr&uuml;nversinnbildlicht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die starken schwarzen Linien zwischen denStreifen unterstreichen&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;das Aufw&auml;rtsschreiten der neuenNation.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Fahne, die zweimal so lang wie breitist,&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;kann in jeder Richtung in vier Teileaufgeteilt und entsprechend gefaltet werden.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Das schwarze Viereck neben derFahnenstange&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;nimmt ein Viertel der L&auml;nge ein&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und ist ein Sinnbild der finsteren Zeiten derVergangenheit;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;es trauert um jene, die ihr Leben lie&szlig;enund kontrastiert&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;mit den bunten Farben der wieder erstandenenNation von Ost-Timor.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die waagerechten Regenbogenfarben flatternsch&ouml;n im Wind&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;und strahlen dabei die Freundschaft aus,&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;die das unabh&auml;ngige Ost-Timor allenNationen der Welt entgegenbringt.&lt;/p&gt;').
text_contents_ge(130,'&lt;style type=&quot;text/css&quot;&gt;
 p.c2 {line-height: 150%} span.c1 {font-size: 12pt; line-height: 150%}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In Sache&lt;strong&gt;EU&lt;/strong&gt; wird die &ouml;sterreichische&Ouml;ffentlichkeit von den staatlichen Medien einer dogmatischenGehirnw&auml;sche ohnegleichen ausgesetzt. Es herrscht imFernsehen, im Radio und fast in der gesamten Presse eine infameverschleierte Zensur.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich w&uuml;rde das auchnicht in einer Demokratie f&uuml;r m&ouml;glich halten, h&auml;tteich diese Art von Zensur nicht selbst erlebt. Unter allerleiPr&auml;texten und Vorw&auml;nden wurde ich mehrmals behindert undverhindert,&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;&ouml;ffentlich&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;eine Anti-&lt;strong&gt;EU&lt;/strong&gt;-Meinung zuvertreten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Vorw&auml;nde waren:es passe nicht in die Sendung, ich d&uuml;rfe nichts vorlesen, ichw&auml;re daf&uuml;r nicht zust&auml;ndig, ich w&auml;reverr&uuml;ckt oder aber man versucht mit gezielter Diffamation michunglaubw&uuml;rdig zu machen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dabei sind meine Bedenkenund &Auml;ngste in den &lt;strong&gt;EU&lt;/strong&gt;-Staaten selbstl&auml;ngst best&auml;tigt. Warum h&ouml;rt man nicht auf LeopoldKohr (Das Ende der Gro&szlig;en), Ernst Friedrich Schumacher (Smallis beautiful), Jimmy Goldsmith (Le pi&egrave;ge - et si nous avionssuivi la mauvaise route?), Henry Coston (L&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;&#039;&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;europe des banquiers), Jean PierreChevenement, Margaret Thatcher, Philippe Seguin, Marie-FranceGaraud, Elisabeth Guigou, Alberto Miele (Cacciamo i mercanti daltempio dell&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;&#039;&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;europa), Gerard de Selys, Dr. Manfred Brunner(Anti-Maastricht-Partei), Sir Ralf Dahrendorf (Ex-EU-Kommissar:&quot;k&uuml;nstliche Superkonstruktion nicht lebensf&auml;hig&quot;), Denisde Rougemont (L&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;&#039;&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;europe doit se faire pour la diversit&eacute;) undunz&auml;hlige andere Warner.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Ouml;sterreich ist vonder Geschichte auserw&auml;hlt, Beispiel und Zentrum und Halt inMitteleuropa zu sein. &Ouml;sterreich ist eine Br&uuml;cke derHoffnung zum Osten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir haben seit dem Kriegin m&uuml;hevoller Arbeit ein riesiges Kapital an Wohlstand,innerem Frieden, internationalem Respekt und Glaubw&uuml;rdigkeitaufgebaut als unabh&auml;ngige, liebenswerte, friedliebendeKulturnation im Herzen Europas. Das kann nicht kurzsichtig undverantwortungslos aufs Spiel gesetzt werden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Gerade jetzt nach demZerfall der kommunistischen Diktatur und der &Ouml;ffnung derGrenzen muss sich &Ouml;sterreich seiner Verantwortung in Europabewusst werden und darf sich nicht selbst eliminieren.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Mit einer uns suggeriertenTorschlusspanik wird eine Anschlussneurose erzeugt. Angst vor wasoder wem? Welche Feinde hat &Ouml;sterreich? Wer sollte und warum&Ouml;sterreichs Sicherheit gef&auml;hrden? Gerade durch denAnschluss an einen Milit&auml;rblock w&uuml;rde &Ouml;sterreich als&quot;Randgebiet Ostmark&quot; erst zum Aufmarschgebiet und zum Feindbild undAngriffsziel werden (Warnung von Erwin Lanc). Gegen atomareBedrohung ist kein Kraut gewachsen und bei Regionalkriegen - sieheBosnien - gibt es keinen &lt;strong&gt;EU&lt;/strong&gt;-Schutz, nicht einmalin Nordirland, innerhalb der &lt;strong&gt;EU&lt;/strong&gt; selbst.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Ouml;sterreichsNeutralit&auml;t ist gerade jetzt n&ouml;tiger als je zuvor. Manmuss die Neutralit&auml;t beim Namen nennen und zu ihr stehen.Neutralit&auml;t ist ein wahrer Wert. Die immerw&auml;hrendeNeutralit&auml;t ist das Fundament unseres Staates und darf nichtverraten und ausgeh&ouml;hlt werden. Wenn wir dieses Fundamentunterminieren, zerst&ouml;ren wir diesen unseren Staat&Ouml;sterreich, der darauf errichtet ist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ab jetzt bedeutet dieNeutralit&auml;t &Ouml;sterreichs Selbst&auml;ndigkeit,Unabh&auml;ngigkeit, Freiheit, Verantwortung tragen, Beispielgeben, eine aktive Neutralit&auml;t.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auch hier ist die Schweizein Beispiel und wird auch im Staatsvertrag als Beispielgenannt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es besteht kein Anlass,gerade jetzt einen Graben quer durch Europa zu ziehen. Es ist soals ob &ouml;stlich von Wien Feindesland w&auml;re.&lt;/span&gt;&lt;em&gt;&lt;span class=&quot;c1&quot;&gt;Die Grenzposten werden von Deutschland an deneben beseitigten Eisernen Vorhang verlegt!&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp; Diese neue Grenzziehung ist nicht nur quer durchEuropa, sondern quer durch &Ouml;sterreich, wo die Donaumonarchiewar. Es w&auml;re verantwortungslos und gef&auml;hrlich, durch denAnschluss an eine wirtschaftliche und politische &Uuml;bermacht dasGef&auml;lle zu und die Bedrohung f&uuml;r die L&auml;nder&quot;drau&szlig;en&quot; zu vergr&ouml;&szlig;ern. &Ouml;sterreichskulturelle, wirtschaftliche und geschichtliche Bestimmung,&Ouml;sterreichs Verantwortung und Ausstrahlung liegt imOsten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;(Das besagt auch der Name&Ouml;sterreich.)&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Fronten haben sichgewandelt. Es sind nicht mehr die politischen Grenzen, sondern dieFront verl&auml;uft zwischen der brutalen, nurwirtschaftsinteressierten Wegwerfgesellschaft und der ausgebeutetenNatur und der zerst&ouml;rten Seele des Menschen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es gibt einen neuenWiderstand und schon wieder feige Kollaborateure.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die jetzt verantwortlichenPolitiker sind Hampelm&auml;nner der Wirtschaftsinteressen undheucheln Umweltbewusstsein und Kulturbewusstsein mitLippenbekenntnissen. Sie beschlie&szlig;en umweltzerst&ouml;rendeund kulturzerst&ouml;rende Ma&szlig;nahmen, die unsere Zukunft inFrage stellen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die sogenannte&lt;strong&gt;EU&lt;/strong&gt; betreibt die brutale Vernichtung unsererLebensbasis, unserer uralten Kulturen, unserer kleinen, gewachsenenEigenst&auml;ndigkeiten, unseres Artenreichtums und unsererSelbstachtung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese &lt;strong&gt;EU&lt;/strong&gt;ist eine riesige Handelsvertretung des Gro&szlig;unternehmertums,der Gewerkschaften, Genossenschaften, der Gro&szlig;banken, derGiftkonzerne, der Monokultur-Monopolisten und der Atom-&lt;/span&gt;&lt;em&gt;&lt;span class=&quot;c1&quot;&gt;und Gen-&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;L&lt;/span&gt;&lt;span class=&quot;c1&quot;&gt;obby, die uns wie brave Schafegemeinsam wie das Schlachtvieh in einen Zug in die falsche Richtungverladen will.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dieser riesigenWirtschaftsmafia geht es nur um Macht und Geld und nicht um dasWohl der Natur und des Menschen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Eine vonverantwortungslosen Schreibtischstrategen ferngesteuertegigantische Fehlplanung zerst&ouml;rt die Fundamente unseres&uuml;ber uralte Zeiten gewachsenen Europas.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Europa erlebt diegr&ouml;&szlig;te Vergewaltigung seiner Geschichte.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Aus Br&uuml;ssel kommtkeine geistige und kulturelle Erneuerung Europas. Von dort kommenkeine Antworten auf die Fragen, die das Gewissen der V&ouml;lkerEuropas beunruhigen (Alberto Miele).&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Aus dieser neuen Hegemoniekommen keine Botschaften, die die Identit&auml;t, unsere uraltenWerte, die Inhalte unserer Zivilisation und die Sehns&uuml;chte derMenschen Europas betreffen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wo sind die Kulturen, dieReligionen, die Philosophien, die ein neues europ&auml;ischesZeitalter aus der Taufe heben?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Gerade jetzt, wo unsereGesellschaft die Grenzen des Wachstums erreicht, istDezentralisierung unsere einzige &Uuml;berlebenschance.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ein Anschluss an diese&uuml;berm&auml;chtige Zentralgewalt, die noch immerWirtschaftswachstum als Zentraldogma gegen alle Evidenz durchziehenwill, hat f&uuml;r alle Beteiligten katastrophaleFolgen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wohin diese&lt;strong&gt;EU&lt;/strong&gt; uns mithineinziehen will, ist ein gnadenloserKonkurrenzkampf, ein Amoklauf in eine gro&szlig;e, gef&auml;hrlicheIllusion, in die Sackgasse des Wirtschaftswachstums. Es w&auml;reWahnsinn, diesem exklusiven Klub der Wegwerfgesellschaftbeizutreten, der sich selbst in den M&uuml;ll wirft und ad absurdumf&uuml;hrt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn das Produkt &quot;EU&quot; sogut ist, warum muss man derart intensiv daf&uuml;r werben? Unddieser verd&auml;chtige Zeitdruck: Wir m&uuml;ssen uns sofortenteignen, sonst ist es zu sp&auml;t!&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Alle guten Dinge geschehenlangsam in einem organischen Ablauf von selbst. Einen Baum kann manauch nicht mit Drohungen &uuml;berzeugen und &uuml;berreden,da&szlig; er im tiefen Winter zu bl&uuml;hen hat.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich verstehe nicht, warumman uns mit Gewalt &uuml;berreden will, die &lt;strong&gt;EU&lt;/strong&gt;,dieses sinkende Schiff zu besteigen, und zwar sofort.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese &lt;strong&gt;EU&lt;/strong&gt;ist eine riesige Titanic. Die Analogien zum Untergang der Titanicsind erschreckend. Hier wie dort: Konzeptlosigkeit,Gr&ouml;&szlig;enwahn, Ma&szlig;losigkeit, blindeTechnologiegl&auml;ubigkeit und Geschwindigkeitswahn um jedenPreis. Dort das Rennen um das &quot;blaue Band&quot; in die t&ouml;dlichenEisberge, hier das m&ouml;rderische Wettrennen an die Grenzen desWachstums der Wegwerfgesellschaft, die unsere Umwelt und uns selbstzerst&ouml;rt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;So einen&quot;europ&auml;ischen Ungl&uuml;cksdampfer&quot; besteigt man nicht undvertraut man unser Leben und unsere Zukunft nicht an. Da ist esbesser, man bleibt in einem kleinen, aber man&ouml;vrierbarenBoot.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In praktisch der ganzen&lt;strong&gt;EU&lt;/strong&gt; geht es wild zu: Nordirland, Baskenland,Korsika, Mafia, Comorra, Ausl&auml;nderverfolgungen, Rassenhass undPogrome, Diskriminierung von Gr&ouml;nl&auml;ndern, Macedoniern undBretonen, brutale Streiks, blutige Ausschreitungen,Studentenrevolten, Drogenkriminalit&auml;t, nukleareSt&ouml;rf&auml;lle, verw&uuml;stete Landstriche, Apathie undKatastrophenstimmung, Jugendkriminalit&auml;t, bald 20 MillionenArbeitslose und eine Staatsspaltung in Flamen und Wallonen,ausgerechnet in Br&uuml;ssel, dem Sitz der &quot;Europ&auml;ischenUnion&quot;. Im Vergleich dazu haben das &quot;isolierte &Ouml;sterreich&quot; unddie skandinavischen L&auml;nder, die man unbedingt in die&lt;strong&gt;EU&lt;/strong&gt; treiben will, es &quot;drau&szlig;en&quot; nochgut.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es wird in der&lt;strong&gt;EU&lt;/strong&gt; mit Menschenschicksalen jongliert alsw&auml;ren es Bauern auf dem Schachbrett. Man kann doch freieBauern nicht mit Geld abspeisen und ins Ausgedinge schicken, dasist erniedrigend, oder sie nur noch als Folklore- und Alibibauernben&uuml;tzen, zum Herzeigen f&uuml;r den Tourismus. Das geht gegenEhre und Selbstachtung, das ist &auml;rger als Mord.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Man bezahlt die Bauernf&uuml;r ihren Selbstmord. Die EU verlangt von den Bauern, siesollen ihre eigene uralte Kultur zerst&ouml;ren. Daf&uuml;rbekommen sie Geld. Anders als bei der Katastrophenhilfe wird dieKatastrophe erst mutwillig in Gang gesetzt. Umgebrachte gewachseneStrukturen kann man auch mit sehr viel Geld nicht wiedergutmachen.Tote kann man mit Geld nicht zum Leben erwecken.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Wunsch einigerm&auml;chtiger und konkurrenzf&auml;higer Gro&szlig;industriellernach Eintritt in die &lt;strong&gt;EU&lt;/strong&gt; ist, noch mehrwirtschaftliche Vorteile zu erlangen. Das geht jedoch auf Kostender sowieso schon gef&auml;hrdeten Kleinbetriebe undKleinbauern.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich wei&szlig; aus eigenerErfahrung, dass in den &lt;strong&gt;EU&lt;/strong&gt;-Staaten z.B. inFrankreich nur Agrargenossenschaften &uuml;ber 100 ha eine&Uuml;berlebenschance haben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das schaut dann so aus:entweder brachliegende, ver&ouml;dete Felder, verlasseneD&ouml;rfer oder Nivellierung von Terrain, Beseitigung von f&uuml;rGro&szlig;maschinen st&ouml;rende W&auml;ldchen, B&auml;umen undFelsen, Nivellierung von uralten Geh&ouml;ften, Begradigung vonB&auml;chen. Eine perfide Zerst&ouml;rung, die in ihrengigantischen Auswirkungen jeden Krieg &uuml;bersteigt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Vermehrte Verwendung vonKunstd&uuml;nger, Pestiziden, genetischen Manipulationen,Bestrahlungen und Umweltgiften aller Art, nat&uuml;rlich in der&lt;strong&gt;EU&lt;/strong&gt; erzeugt. Eine sadistische, technokratischeZerst&ouml;rung jeglicher Romantik mit dem Schlagwort: unrentabel.Monokultur, so weit das Auge reicht. Dem Bauern wird befohlen,unsere &Uuml;berlebensgrundlage systematisch zu vergewaltigen, denihm zu treuen H&auml;nden anvertrauten Grund und Boden, Wald undFluren zu vergiften und zu zerst&ouml;ren. Die Landwirtschaft wirdzur gigantischen Agro-Business-Industrie.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Abh&auml;ngigkeit derBauern und von uns allen von Industrie, Chemie undMonopolwirtschaft, diese neue Leibeigenschaft, wird durch einenAnschluss an die &lt;strong&gt;EU&lt;/strong&gt; ins Unertr&auml;glichegesteigert.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ausl&auml;ndische,m&auml;chtigere, brutalere Befehlshaber treten an die Stelle dereinheimischen kleineren Firmen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ebenso ergeht es denKleineren, d. h. allen menschlichen Betrieben, sie werdenvernichtet, daf&uuml;r gibt es noch mehr Gro&szlig;kaufh&auml;user,Gro&szlig;banken und Gro&szlig;industrialisierung. Kurzum alles,was unser aller Leben romantisch, liebens- und lebenswert macht,hat kaum mehr &Uuml;berlebensm&ouml;glichkeit. Die stupidenBrutalit&auml;ten siegen, die delikaten Werte kommen um, weil siesich nicht wehren k&ouml;nnen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DieAgro-Business-Industrie in der EU betreibt fast analog dem&bdquo;Dritten Reich&quot; eine brutale Auslese von reinrassigengleichgeschalteten &quot;Elite&quot;-Produkten und eine Eliminierung von&quot;minderwertigem&quot; Erbgut oder Saatgut.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es beginnt mit densch&ouml;nen gro&szlig;en &Auml;pfeln, die alle gleich seinm&uuml;ssen, die in Monokultur gez&uuml;chtet sind, in gleicherRasse, mit gleichem Geschmack, und setzt sich fort in dergenetischen &quot;Nutztierveredelung&quot; und den Legehennenbatterien. Allesnicht produktive Leben wird eliminiert. Alle nicht profitableVielfalt, alle Andersartigkeit, alle Kleinteiligkeit, allernutzloser Artenreichtum, alle unrentable Sch&ouml;nheit wirdvernichtet.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dadurch sterben auch dasKleinbauerntum, die Familienbetriebe, die Romantik, kurzum unsereLebensqualit&auml;t.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Durch das Sterben derGrei&szlig;ler&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;(Gemischtwarenhandlungen),&lt;/span&gt;&lt;/em&gt; &lt;span class=&quot;c1&quot;&gt;derBauern, der Familien- und Kleinbetriebe, durch das Sterben derkleinen Identit&auml;ten entsteht eine Gleichschaltung und eineAnonymisierung unserer Gesellschaft. Man raubt dem Menschen seineW&uuml;rde. Dies bringt eine neue Abh&auml;ngigkeit, eine neueVersklavung, eine neue Leibeigenschaft noch nie dagewesenenAusma&szlig;es hervor.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist das Ende deshumanistischen Europas.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Was dann &uuml;brigbleibtist keine menschengerechte, sondern eine maschinengerechte undcomputergerechte Welt. Es ist eine Zerst&ouml;rung von h&ouml;herenWerten, die in ihrer Vielfalt Europa ausmachen. Es ist dieZerst&ouml;rung Europas.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Kleinbauernm&uuml;ssen Haus und Hof und Land verkaufen, werden entwederarbeitslos oder Stadtproletariat oder m&uuml;ssen sichAgro-Business-Genossenschaften anschlie&szlig;en, wo sie nur mehrBefehlsempf&auml;nger ohne Entscheidungsfreiheit sind, auf einemLand, das einmal ihnen geh&ouml;rte.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die &lt;strong&gt;EU&lt;/strong&gt;leidet unter einem wachsenden Arbeitslosenheer, das schon jetzt derarbeitenden Bev&ouml;lkerung eines Landes 7- bis 10-mal sogro&szlig; wie &Ouml;sterreich entspricht!&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Man kann sich vorstellen,welches verheerende Beispiel die &lt;strong&gt;EU&lt;/strong&gt; damit der Weltgibt, wenn die 3 Milliarden Menschen, die in der Dritten Welt aufdem Lande ihr Auskommen finden, diesem &lt;strong&gt;EU B&lt;/strong&gt;eispielzu folgen gezwungen werden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Eine gigantischeFl&uuml;chtlingswelle von 2 Milliarden Landbev&ouml;lkerung -diesen &quot;exode rural mondial&quot;, wie Jimmy Goldsmith warnt, k&ouml;nnedie Welt nicht mehr ertragen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Grenzen sind so wieunsere Haut. Sie sch&uuml;tzen den gewachsenen Organismus, unsereindividuelle Andersartigkeit vor Gefahr von Au&szlig;en wie Hitzeund K&auml;lte. Wenn man die Haut entfernt, stirbt der Organismus,weil er schutzlos wird.&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;Daher haben wirSchutzz&ouml;lle zum Schutz einer gef&auml;hrdeten Gemeinschaft.&auml;hnlich wie wir Sonnenbrillen und Zeckenimpfungen etc. zuunserem Schutz einsetzen.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;em&gt;&lt;span class=&quot;c1&quot;&gt;Was Gott getrennt hat,sollen wir nicht mit Gewalt einen.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es gibt nat&uuml;rlicheGrenzen wie zum Beispiel die Sahara, das Mittelmeer und die Alpen,in deren Schutz sich uralte Kulturen gebildet haben wie dieafrikanischen, die mediterranen und die nordeurop&auml;ischenKulturen. Die Alpen haben eine Schutzfunktion sowohl f&uuml;r denNorden als auch f&uuml;r den S&uuml;den.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ein schrankenloser,sinnloser Transitverkehr ist gleichbedeutend mit einemNiederrei&szlig;en und Nivellieren der Alpen, auch wenn der Verkehruntertunnelt wird. &Ouml;sterreich hat genauso wie die Schweiz eineAlpine Schutzw&auml;chterfunktion f&uuml;r Europa.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die fieberhafte,unnat&uuml;rliche, konzeptlose Hin- und Herraserei ist Wahnsinn undwird in der &lt;strong&gt;EU&lt;/strong&gt; zum &quot;grenzenlosen Wahnsinn&quot;. An demOrt, wo wir sind, machen wir alles hin, dann fl&uuml;chten wir vorunserer eigenen Zerst&ouml;rung, um anderswo alles hin zu machen,und auf dem Weg dahin wird auch alles hin. Dieser falsche Trendwird in der &lt;strong&gt;EU&lt;/strong&gt; forciert undunterst&uuml;tzt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Elisabeth Guigoudr&uuml;ckt das so aus: Eine Wirtschaft, die nur aufKurzzeit-Rentabilit&auml;t ausgerichtet ist, ohne Plan, ohneVision, ohne Sinn, die arbeitsstellen-fressende Maschinenproduziert, die mehr und mehr von der Transport- undKommunikationsgeschwindigkeit abh&auml;ngig ist und die unterAu&szlig;erachtlassung des &ouml;kologischen Gleichgewichtes dieMenschen zu einer ebenso forcierten Beweglichkeit zwingt wie dieWaren, die Kapitalien, die Technologien und Informationen unterMissachtung der Menschenw&uuml;rde und des Schutzes der gewohntenLebensweisen, eine &ouml;konomische Diktatur, die &uuml;ber alleanderen menschlichen T&auml;tigkeiten triumphiert...&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn man das, was man zusch&uuml;tzen hat, vorher umbringt, braucht man keinen Schutz mehr.Zuerst m&uuml;ssen die Bauern und die Grei&szlig;ler&lt;/span&gt;&lt;em&gt;&lt;span class=&quot;c1&quot;&gt;(Gemischtwarenhandlungen)&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;weg und die Kleinbetriebe und der &quot;nutzlose&quot;Artenreichtum, die &quot;unrentable&quot; Vielfalt. Zuerst m&uuml;ssen wiruns angleichen an internationales Mittelma&szlig;, an vergiftetesEssen, genmanipulierte und bestrahlte Lebensmittel. Zuerstm&uuml;ssen wir in vorauseilendem Gehorsam uns selbst nivellierenund uns vernichten, dann kann die &lt;strong&gt;EU&lt;/strong&gt;widerstandslos einmarschieren und durchmarschieren.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Ouml;sterreich als ganzeswird ein Vasallenstaat, eine neue Ostmark. Selbstverst&auml;ndlichist dann &Ouml;sterreichs Position als neutrale, kernkraftfreie,umweltbewusste Nation kaum mehr haltbar. &Ouml;sterreich kann dannnicht mehr selbst entscheiden, was es tut, was und wieviel esproduziert und in welche L&auml;nder es exportiert und aus welchenes importiert. &Ouml;sterreichs Geschicke werden von fremden Leutenin Br&uuml;ssel oder anderswo in der &lt;strong&gt;EU&lt;/strong&gt; bestimmt,von Schreibtischstrategen, die sich einen Dreck um &Ouml;sterreichsEigenst&auml;ndigkeit scheren.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Uuml;ber &Ouml;sterreichsNeutralit&auml;tswillen, &uuml;ber &Ouml;sterreichsUmweltbewusstsein wird in der &lt;strong&gt;EU&lt;/strong&gt;-Kommandozentralenur gelacht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Slogan &quot;einsam odergemeinsam&quot; ist eine angstmachende, demagogische Aufforderung zurVerunsicherung, zur Unfreiheit und zur Abh&auml;ngigkeit. Es magparadox klingen, aber allein ist jeder am st&auml;rksten. Auspers&ouml;nlicher Erfahrung wei&szlig; ich, dass einAussenstehender genauso stark ist wie die Gruppe und nichtschw&auml;cher. Der Au&szlig;enstehende ist beweglicher, effektiverin seinem Handeln und im Reagieren als eine schwerf&auml;llige,b&uuml;rokratische, chaotische Organisation.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Europa ist nur stark dankder Vielfalt seiner Eigenst&auml;ndigkeiten. Diese kleinteiligeVielfalt muss verst&auml;rkt und gesch&uuml;tzt werden. DieseVielfalt ist Europa. Schon de Gaulle erkannte, dass Europa nur eineChance als Europa der selbst&auml;ndigen, freien Vaterl&auml;nderhat.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es istunverst&auml;ndlich, wie verantwortungslos unsere Politiker jetztunser wertvollstes Gut, unsere Unabh&auml;ngigkeit, Freiheit undEigenst&auml;ndigkeit aufgeben und &Ouml;sterreich entm&uuml;ndigenwollen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist ein Verrat an&Ouml;sterreich.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Beim letzten Anschluss gabes auch jede Menge Seyss-Inquarts, Guislings, Petains, die in derdamaligen Situation wahrscheinlich auch aus &Uuml;berzeugung undPatriotismus glaubten, das Richtige zu tun und ihre Pflichterf&uuml;llen zu m&uuml;ssen, indem sie ihr Land einer fremdenHegemonie anvertrauten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Heute lehrt uns dieGeschichte, dass ihr Tun falsch und sch&auml;dlich war, und siesind l&auml;ngst als Verr&auml;ter und Kollaborateure gebrandmarkt,bestraft und hingerichtet worden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Mitgefangen -mitgehangen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Unter der Dunstglocke der&lt;strong&gt;EU&lt;/strong&gt; wird von allen unbemerkt der Anschluss anDeutschland vollzogen. Wenn dann diese &lt;strong&gt;EU&lt;/strong&gt;Schiffbruch erleidet, haben wir noch ein St&uuml;ck mehr unsererUnabh&auml;ngigkeit verloren und bleiben am gro&szlig;en deutschenBruder kleben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Bei Wahrung allerUnterschiede hat &Ouml;sterreich &Auml;hnliches bereits erlebt, alsauch aus wirtschaftlichen Gr&uuml;nden 1937-1938 ein Anschluss andas &quot;Altreich&quot; propagiert wurde. &quot;Heim ins Reich&quot; hie&szlig; damalsdie Parole.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;em&gt;&lt;span class=&quot;c1&quot;&gt;Beim Umtausch desSchilling in den Gro&szlig;macht-Euro muss man fatalerweiseAnalogien mit dem Umtausch des Schilling 1938 in die Reichsmarkfeststellen.&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Man darf nicht vergessen,dass das Versprechen einer finanziellen und wirtschaftlichenBesserstellung der k&uuml;nftigen Ostm&auml;rker Haupttriebfedervieler &Ouml;sterreicher war, um f&uuml;r den Anschluss zustimmen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auf die bejubelteEinverleibung mit Gro&szlig;deutschland erfolgte dieErn&uuml;chterung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Jetzt ist es &auml;hnlich.Genau das, was man uns in der &lt;strong&gt;EU&lt;/strong&gt; vorgaukeln will,verlieren wir in der &lt;strong&gt;EU&lt;/strong&gt;: sozialen Wohlstand,Vollbesch&auml;ftigung, eine intakte Umwelt, unsere hoheLebensqualit&auml;t, unsere Heimat, unsere Selbstachtung, unsereNatur und unsere uralten Kulturen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Europ&auml;er ist man nurdann, wenn man sein unabh&auml;ngiges, eigenst&auml;ndigesEurop&auml;ertum bewahrt und hochh&auml;lt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Schweiz, wohlhabendund demokratisch, auch im Herzen Europas gelegen, soll uns einleuchtendes Beispiel sein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich verstehe auch nicht,wieso unser Bundespr&auml;sident uns zur Aufgabe unsererSouver&auml;nit&auml;t auffordert. Er m&uuml;sste doch als obersterPatriot warnen und die Verfassung sch&uuml;tzen. Ist dennLandesverrat kein Vergehen, wenn man &Ouml;sterreich einerausl&auml;ndischen Zentralgewalt unterwerfen will?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Staatsvertrag brachte&Ouml;sterreich die Befreiung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der &lt;strong&gt;EU&lt;/strong&gt;Vertrag ist eine Kapitulation &Ouml;sterreichs, eineEntm&uuml;ndigung &Ouml;sterreichs, eine Unterwerfung unter eineneue anachronistische Hegemonie - und ein Bruch desStaatsvertrages.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist unglaublich: DiePolitiker bereiten den zweiten Anschluss vor und wollen das&ouml;sterreichische Volk vor vollendete Tatsachen stellen. Sindunsere Politiker von allen guten Geistern verlassen, so dasserst&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;die Geschichte&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;sie zurechtweisen muss?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich bin kein Jurist, aberich bin der festen &Uuml;berzeugung, dass wir von m&auml;chtigenInteressensgruppen au&szlig;erhalb jeder ethischen,geschichtlichen, moralischen, kulturellen und &ouml;kologischenLegalit&auml;t mit einer miesen Demagogie manipuliert werden, sowie seinerzeit bei Zwentendorf&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;(Inbetriebnahme des Atommeilers wurde durch Volksabstimmungverhindert)&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;c1&quot;&gt;.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die&lt;strong&gt;EU&lt;/strong&gt;-Staaten werden nivelliert und anonymisiert undihrer Identit&auml;t beraubt. In diese Richtung geht es und nichtin die Richtung der Dezentralisation und&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;Vertiefung&lt;/span&gt;&lt;/em&gt; &lt;span class=&quot;c1&quot;&gt;derEigenst&auml;ndigkeiten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Zum Beweis gen&uuml;gt die&lt;strong&gt;EU&lt;/strong&gt;-Fahne mit den jetzt 12 genau gleichen5-zackigen Sternchen. Welches dieser Sternchen w&uuml;rde&Ouml;sterreich symbolisieren, etwa das dritte rechts von untenoder das zweite, das genauso aussieht?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der F&uuml;nfstern istkein Symbol aus der europ&auml;ischen und schon gar nicht aus der&ouml;sterreichischen Geschichte. Er kommt in der Entwicklung derStaaten des Okzidents nicht vor. Das Pentagramm, einokkultistisches Zeichen der Druden und Freimaurer ist einFremdk&ouml;rper in der abendl&auml;ndischen Heraldik.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die 5-zackigen Sternedienen eher als Merkmal der Unterwerfung, Inbesitznahme undKolonisierung wie auf der austral-neuseel&auml;ndischen,amerikanischen und auf Kolonialfahnen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese&lt;strong&gt;EU&lt;/strong&gt;-Fahne ist noch dazu ein Plagiat aus demSternenbanner. Auch die Vereinigten Staaten Amerikas waren als5-zackige Sternchen, wei&szlig; auf blau, zuerst im Kreisangeordnet, genauso wie jetzt die M&ouml;chtegern-&quot;VereinigtenStaaten Europas&quot;. Sp&auml;ter, als sie sich vermehrten, wurden siein Reih und Glied gebracht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese 5-zackigen Sternchensind dieselben wie der Sowjetstern, der Stern der jugoslawischen,chinesischen, kubanischen und sonstigen Volksrepubliken. Wahrlich,dieses Symbol ist ein Armutszeugnis f&uuml;r die gewachsenenIdentit&auml;ten Europas.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist ein b&ouml;sesOmen, wenn schon das Zeichen falsch ist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese&lt;strong&gt;EU&lt;/strong&gt;-Sternchenfahne symbolisiert die SelbstaufgabeEuropas.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In der sogenannten&lt;strong&gt;EU&lt;/strong&gt; ist die Kultur weniger als Nebensache. Diesch&ouml;pferischen Kr&auml;fte werden zu n&uuml;tzlichen Idiotengetrimmt. Zur Unterst&uuml;tzung und Verbr&auml;mung undLegitimierung eines merkantilen und industriellen Wachstums, dassich selbst zerst&ouml;rt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Kultur ist inBr&uuml;ssel nicht pr&auml;sent. Sie ist in der nur merkantilen undindustriellen &lt;strong&gt;EU&lt;/strong&gt; kein Thema. In Br&uuml;ssel kenntman den Preis von allem und den Wert von nichts.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es geht in dieserkulturlosen, sogenannten &lt;strong&gt;EU&lt;/strong&gt; nur um Zahlen:Produktionszahlen, Termine und Laufzeiten, &Uuml;bergangsfristen,Einwohner- und Viehzahlen. Bruttosozialprodukte, Anbau-, Wald- undWeidegebiete in Quadratkilometern, Lohnkosten, Sozialleistungen,Nebenkosten. Exportziffern, Tonnenzahlen f&uuml;r Schwertransporte,Milchpreise, Preisanpassungen, Finanzierungszahlen,Ausgleichszahlen, Prozente, Preissenkungen, Zucker-, Milch&shy;-und Fischquoten, Transitbeschr&auml;nkungen in Zahlen, Kontingente,Mitfinanzierungen, Tabellen, Statistiken,Marktzugangsbeschr&auml;nkungen in Zahlen. Laufzeiten, EcuUmtauschkurse, hohe Arbeitslosenzahlen, niedrigeBesch&auml;ftigungszahlen, F&ouml;rderungs- und Subventionszahlen,die Anzahl von &Ouml;kopunkten, Geldzahlungen aller Art, irrealeWirtschaftswachstumszahlen, Umfrageergebnisse in Zahlen undProzenten, Stimmenanzahlen des Ministerrates und derSperrminorit&auml;t, Mehrheiten, Minderheiten,Abstimmungsergebnisse, EU-Werbeausgaben. Ja sogar H&ouml;henzahlenf&uuml;r Bergbauern in Metern und Breitegradzahlen f&uuml;r dieLandwirtschaft in Finnland.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn das nichtsymptomatisch ist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auch der Umweltschutz istin der Br&uuml;sseler Hegemonie abwesend. Nur 0,3 Prozent (30 von10.000) der &lt;strong&gt;EU&lt;/strong&gt;-Beamten sind f&uuml;r die&Ouml;kologie t&auml;tig. Vom Grenzen &uuml;berbr&uuml;ckendenUmweltschutz ist nur die Rede. &Ouml;sterreich muss die Kernkraftmitfinanzieren und z. B. Plastikflaschen&lt;/span&gt; &lt;em&gt;&lt;span class=&quot;c1&quot;&gt;und Gen-Produkte&lt;/span&gt;&lt;/em&gt; &lt;span class=&quot;c1&quot;&gt;undidiotisch-gef&auml;hrliche Richtlinien f&uuml;rApfelgr&ouml;&szlig;en und Monokultur-Obstsorten &uuml;bernehmen.Das habe ich selbst gesehen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Von Kultur und den Wertendes Abendlandes ist in Br&uuml;ssel keine Rede. Um Ausgabenf&uuml;r Kultur muss man erst Br&uuml;ssel bitten. DasKulturstudium und auch der gesamte Studienbereich in der&lt;strong&gt;EU&lt;/strong&gt; wird ausgerichtet auf die N&uuml;tzlichkeitf&uuml;r die Industrie und auf das forcierte Wachstum.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nur die Sch&ouml;pfungkann uns Kreativit&auml;t und organisches Wachstum lehren. Nur dieSch&ouml;pfung kann uns Achtung lehren vor dem Artenreichtum undder unglaublichen Vielfalt, deren Teil wir selbst sind.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In der &lt;strong&gt;EU&lt;/strong&gt;aber gehen die Bestrebungen in die genau umgekehrte Richtung:Vernichtung der kleinteiligen sch&ouml;pferischen Werte zugunsteneiner v&ouml;llig falschen und &uuml;berholten Automatisierung,Zentralisierung und Rationalisierung, die uns &uuml;ber kurz oderlang zum Untergang bringt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Gewissen unsererZivilisation, unsere uralte Bestimmung, h&ouml;here Werteanzustreben, bleibt auf der Strecke.&lt;/span&gt;&lt;/p&gt;').
text_contents_ge(131,'&lt;style type=&quot;text/css&quot;&gt;

&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;Schon in seinem Text von 1988&bdquo;Anschlu&szlig;psychose&ldquo; formulierte Hundertwasserseine &Uuml;berzeugung, da&szlig; ein Anschlu&szlig; an die EG(Europ&auml;ische Gemeinschaft) keine gute Sache f&uuml;r&Ouml;sterreich w&auml;re und da&szlig; &Ouml;sterreich im Falleeines Beitritts zur EG seine Eigenst&auml;ndigkeit, seineUnabh&auml;ngigkeit, seine Neutralit&auml;t und seineGlaubw&uuml;rdigkeit als friedliebende Kulturnation im HerzenEuropas verlieren w&uuml;rde.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;In den Jahren 1993 und 1994 trat Hundertwasserimmer wieder vehement gegen einen Beitritt &Ouml;sterreichs zurEurop&auml;ischen Union ein und formulierte seine Bedenken in demManifest &lt;em&gt;EU Beitritt ist Verrat an &Ouml;sterreich und Verratan Europa.&lt;/em&gt;&lt;/p&gt;').
text_contents_ge(132,'&lt;style type=&quot;text/css&quot;&gt;
 span.c1 {font-size: 12pt; font-family: &quot;Times New Roman&quot;}
&lt;/style&gt;
&lt;span class=&quot;c1&quot;&gt;Seine Gegnerschaft gegen den Beitritt&Ouml;sterreichs zur Europ&auml;ischen Union war radikal undleidenschaftlich&hellip;. Er hat die Anti-Maastricht-Stellungnahmeneiniger Autoren und Politiker aufmerksam verfolgt, und er hat siein den besonderen Kreis der europ&auml;ischen vierten Hautaufgenommen. Zu nennen w&auml;re in diesem Netzwerk derWahlverwandtschaft: Jimmy de Goldsmith (&bdquo;DieFalle&ldquo;)&hellip;.Leopold Kohr (&bdquo;Das Ende derGro&szlig;en&ldquo;), E. F. Schumacher (&bdquo;Small IsBeautiful&ldquo;), Henry Coston (&bdquo;Das Europa derBankiers&ldquo;), Alberto Miele (&bdquo;Jagen wir die H&auml;ndleraus dem Tempel Europas&ldquo;) oder Denis de Rougemont(&bdquo;Europa mu&szlig; sich durch Verschiedenheitauszeichnen&ldquo;). Unter den Politikern gilt seine Sympathie deneisernen Damen der nationalen Identit&auml;t, Margaret Thatcher,Marie-France Garaud und Elizabeth Guigou.&lt;/span&gt;').
text_contents_ge(133,'&lt;style type=&quot;text/css&quot;&gt;

&lt;/style&gt;
Hundertwasser ist erst sp&auml;t zu einem Bewu&szlig;tsein dervierten, famili&auml;ren Haut gelangt. Als Einzelkind, dasfr&uuml;h den Vater verlor, war sein Familienleben auf dieBeziehung zu seiner Mutter beschr&auml;nkt, eine mit einer tiefenironischen Z&auml;rtlichkeit durchsetzte instinktive Verbundenheit.&hellip;Als sie 1972 stirbt, erreicht Hundertwasser einenentscheidenden Wendepunkt in seiner Karriere, und nun wird erf&uuml;r das soziale Umfeld und die Identit&auml;tsprobleme vielaufgeschlossener, die mit der Zugeh&ouml;rigkeit zu einer Gruppe,einer Gemeinschaft, einer Nation einhergehen. &hellip; F&uuml;rHundertwasser bleibt die vierte Haut nicht auf die Ebene derFamilie beschr&auml;nkt, sei diese die nat&uuml;rlich oder eineerworbene. Das soziale Umfeld erstreckt sich auf die Gesamtheit derKontakte, die das Leben eines Kollektivs ausmachen. &hellip; Unddieser &ouml;sterreichische Maler f&uuml;hlt sich als B&uuml;rgereiner Welt, von der er ein neues Bild schafft, das Bild eines inSch&ouml;nheit gl&uuml;cklichen Raumes. Aus der Sch&ouml;nheitentsteht der Friede: Der Friede in Sch&ouml;nheit ist dersouver&auml;ne Ausdruck der gro&szlig;en nat&uuml;rlichen Ordnung,der universellen Harmonie. &hellip; Die geniale KraftHundertwassers liegt in der Homogenit&auml;t und Geschlossenheitseiner globalen Sicht. Hundertwassers Geopolitik beruht aufnationaler Identit&auml;t, auf der Gesamtheit der verschiedenenZeichen, die die spezifische Eigent&uuml;mlichkeit einer Nationkenntlich machen.').
text_contents_ge(134,'&lt;style type=&quot;text/css&quot;&gt;
 div.c5 {border-right: medium none; padding-right: 0cm; border-top: medium none; padding-left: 0cm; padding-bottom: 1pt; border-left: medium none; padding-top: 0cm; border-bottom: 1.5pt solid} p.c4 {padding-right: 0cm; padding-left: 0cm; padding-bottom: 0cm; line-height: 150%; padding-top: 0cm; border: medium none} p.c3 {margin-bottom: 10.8pt; line-height: 150%} p.c2 {margin-bottom: 7.2pt; line-height: 150%} p.c1 {line-height: 150%}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal c1&quot;&gt;ich Ich esse nicht gerne Kirschen, da&lt;&ndash;&ndash; die Stengel wegwerfen und die Kerne ausspuckenmu&szlig;&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich ich esse nicht gerne Bananen, da&lt;&ndash;&ndash; die Schalen wegwerfen mu&szlig;&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich ich esse nicht gerne N&uuml;sse, da&lt;&ndash;&ndash; die Schalen wegwerfen mu&szlig;&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich esse nicht gerne Orangen, da dieSchalen zu bitter sind&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich esse gerne &Auml;pfel und Birnen&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich esse gerne Zitronen, ganz mit derSchale, in Scheiben ganz fein geschnitten und mit Zucker&lt;/p&gt;&lt;p class=&quot;MsoNormal c2&quot;&gt;ich esse gerne Kartoffeln (mit Schalen)(und das Gem&uuml;se- und das Kartoffelwasser soll man nichtwegsch&uuml;tten, sondern trinken)&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich ich kaufe nicht gerne Farben in Tubenund in Tiegeln, in Sch&uuml;sserln und H&auml;ferln, weil&lt;&ndash;&ndash; das Blech und Porzellan wegwerfen mu&szlig;&lt;/p&gt;&lt;p class=&quot;MsoNormal c3&quot;&gt;ich male nicht gerne mit Farben ausT&uuml;ten, weil dann das Bild die Schalen nicht mehrfri&szlig;t&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich male gerne mit echten und teuren undreinen Farben, die man, oft schon zersto&szlig;en, per Dekagrammkauft&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich male aber auch gerne mit KnopffarbenG&uuml;nther Wagner (die kleinen Papiere hinten kann man abkletzelnund schlucken)&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich vermale alle Farben bis auf die letzteQuantit&auml;t eines Stecknadelkopfes&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich ich male alle Farben nebeneinander,denn es w&auml;re schade, sie gegenseitig zuzudecken dadurchda&szlig; &lt;&ndash;&ndash; sie &uuml;bereinander male,au&szlig;er man kann sehr gut hindurchsehen&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich das Wasser, mit dem &lt;&ndash;&ndash;die Palette abwasche, sch&uuml;tte&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich &lt;&ndash;&ndash; nicht weg, sondernsch&uuml;tte und streiche auf das Bild,&lt;/p&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;ich auf dem &lt;&ndash;&ndash; auch diePinsel abwische&lt;/p&gt;&lt;div class=&quot;c5&quot;&gt;&lt;p class=&quot;MsoNormal c4&quot;&gt;ich so bin &lt;&ndash;&ndash; sicher,da&szlig; keine Farbe verlorengeht&lt;/p&gt;&lt;/div&gt;&lt;p class=&quot;MsoNormal c1&quot;&gt;9&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 13&lt;/p&gt;').
text_contents_ge(135,'&lt;style type=&quot;text/css&quot;&gt;
 span.c2 {text-decoration: underline} span.c1 {font-size: 12pt; font-family: &quot;Times New Roman&quot;}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Zeit hat sich gewandelt,die Kompetenzen haben sich verschoben. Die, die Macht hatten, habenkeine Macht mehr - sind entmachtet. Die tats&auml;chliche Machtliegt nicht bei denen, die Kanonen oder Geld haben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nicht nur das.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die sogenanntenVerantwortlichen wissen nicht mehr, was sie tun.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Den Architekten entgleitetdie Verantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den &Auml;rzten entgleitetdie Verantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den Bauern entgleitet dieVerantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den G&auml;rtnern undBotanikern entgleitet die Verantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den Politikern entgleitet dieVerantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;denReligionsrepr&auml;sentanten entgleitet die Verantwortung ihresTuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den Wissenschaftlernentgleitet die Verantwortung ihres Tuns,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den &Ouml;konomen entgleitetdie Verantwortung ihres Tuns.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;den Erziehern entgleitet dieVerantwortung ihres Tuns.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sie sind alle schuldig. DieListe der Schuldigen l&auml;&szlig;t sich beliebig langefortsetzen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Architekten bauenverbrecherisch und menschenunw&uuml;rdig oder zu Beton gewordeneSchnapsideen. Sie bauen Gef&auml;ngniszellen, in denen die Seeledes Menschen zugrunde geht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die &Auml;rzte arbeiten mitGiften und Gegengiften. Wenn der Spezialist &uuml;ber seinenkleinen Bereich hinaussehen will, sieht er nichts, denn er istblind.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Bauer vergiftetsystematisch unsere &Uuml;berlebensgrundlage. Er vergiftet den ihnzu treuen H&auml;nden anvertrauten Grund und Boden, Wald und Flurenmit einem Agrobusinessdenken von satanischenAusma&szlig;.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;G&auml;rtner und Botanikererkennen nicht das Gebot der Stunde, die Pflicht, die sie geradejetzt zu erf&uuml;llen h&auml;tten und versuchen krampfhaft, echtePflanzen den Plastikblumen anzugleichen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Politiker lebt nach demMotto: NACH MIR DIE S&Uuml;NDFLUT.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Von Zukunft hat er keineAhnung, weil er aus der Geschichte nichts gelernt hat. DieserOpportunismus erzeugt Terrorismus und Kriege.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Priester aller Religionenpredigen Dogmen, die oft ung&uuml;ltig geworden sind, die sogarsch&auml;dlich, unmenschlich und teuflisch sind.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Wissenschaftler und&Ouml;konomen handeln noch immer nach dem Gesetz des Raubbaus unds&auml;gen an dem Ast, auf dem sie und wir alle sitzen. Noch immerlaufen sie dem rationellen und funktionellen Trugbild nach undlaufen sich zu Tode.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Erzieher wissen nicht,da&szlig; unser wahres Analphabetentum die Unf&auml;higkeit ist,wahrhaft sch&ouml;pferisch t&auml;tig zu sein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich m&ouml;chtezusammenfassen:&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir leben in einer Zeit, inder die Kompetenzen nicht mehr stimmen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Arzt kann nicht mehrheilen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Lehrer erzeugtsch&ouml;pferische Impotenz.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Wissenschaftler kannnicht erkennen, da&szlig; seine Erfindung f&uuml;r die Katz&lsquo;ist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Architekt ist ein feigerHampelmann, gehorsamer Erbauer von Krebsgeschw&uuml;renaus&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Beton.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Politiker ist eineEintagsfliege, kurzsichtig, machtlos und unf&auml;hig.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Priester hat seineFunktion verloren&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Bauer vergewaltigt,verw&uuml;stet und verkauft seine eigene Erde&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;+++&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In dieser Situation mu&szlig;es eine Gruppe von Menschen geben, die ungefragt Verantwortung&uuml;bernimmt. Das waren fr&uuml;her einmal die Priester. Heutesind es alle, die selbst&auml;ndig sch&ouml;pferisch sind. Siedenken religi&ouml;ser als Priester, umsichtiger alsStadtplaner.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Kreativ und sch&ouml;pferischsein hei&szlig;t frei sein und sich selbst verwirklichen imEinklang mit der Natur.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Um gl&uuml;cklich zu sein,braucht der Mensch keinen &auml;u&szlig;eren Reichtum, sonderneinen inneren Reichtum der Seele.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Um gl&uuml;cklich zu sein,braucht der Mensch keine mechanische Energie, sondern eine inneresch&ouml;pferische Energie.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der heutige Mensch ist dergef&auml;hrlichste &lt;em&gt;Sch&auml;dling&lt;/em&gt;, der je die Erdebev&ouml;lkert hat.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch ist ein&ouml;kosystemfremder Sch&auml;dling geworden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Perfekte &Ouml;kologiem&uuml;&szlig;te sein, den Menschen in seine &ouml;kologischenSchranken zu verweisen, damit sich die Erde regenerierenkann.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der wahnsinnige,unbegr&uuml;ndete Energieverbrauch des Menschen m&uuml;&szlig;teeiner&nbsp; verantwortungsbewu&szlig;ten, sch&ouml;pferischenIntelligenz entsprechen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dem ist aber nichtso.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch ist ein dummesHerdentier geblieben, das pl&ouml;tzlich irrsinnige Mengen vonEnergie, von Giften und anderen Mordmitteln zur Verf&uuml;gung hat,die es wild verpulvert oder r&uuml;cksichtslos zur Vernichtung derUmwelt und der eigenen Br&uuml;der einsetzt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Und gierig verlangt dieserMensch, dieses dumme Herdentier, nach &lt;span class=&quot;c2&quot;&gt;nochmehr&lt;/span&gt; Energie, &lt;span class=&quot;c2&quot;&gt;noch mehr&lt;/span&gt; Giften und&lt;span class=&quot;c2&quot;&gt;noch mehr&lt;/span&gt; Mordmitteln.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Atomenergie ist einewirtschaftliche Katastrophe und eine &ouml;kologische Katastrophe.Atomenergie kommt uns teuer zu stehen, sowohl wirtschaftlich alsauch &ouml;kologisch.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch, besonders diesogenannten Experten, haben die Kontrolle &uuml;ber dieEnergiehebel verloren. Sie wissen nicht mehr, was sietun.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es gibt kein sicheresVersteck oder Depot f&uuml;r nuklearen Abfall, auch nicht inscheinbar vorl&auml;ufig erdbebenfreien Gebieten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wissen wir, wo die Inkas, wodie Karthager Dinge vergraben haben? Und das ist erst 2000 Jahreher. Wissen wir, wo unsere Gro&szlig;mutter ihre Goldm&uuml;nzenversteckt hat? Und das ist erst 50 Jahre her. Nuklearer Abfall aberbleibt 500.000 Jahre todesgef&auml;hrlich f&uuml;r allesLeben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Atomm&uuml;ll l&auml;&szlig;tsich nicht vergraben wie eine sch&ouml;ne Leich&lsquo;.Atomm&uuml;ll wird nicht zu Humus.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Einstein hat gesagt: Wenn dieFormel nicht sch&ouml;n ist, kann sie auch nicht richtigsein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist genau das Gegenteilvon dem, was die Funktionalisten, Rationalisten und Technokratenpredigen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nicht ohne Grund waren dieersten technischen Einrichtungen vor hundert Jahren noch schamhaftverkleidet - die Autos als Pferdekutschen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Maschinen, Aufz&uuml;ge,Telefone, Gaslaternen, elektrischen Luster, Metro- undStadtbahnaufg&auml;nge waren Kunstwerke, die jetzt im Museumstehen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Man wu&szlig;te damals sehrgenau, da&szlig; technische Rationalit&auml;t eine S&uuml;nde ist,und hat sie hinter Kunst versteckt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Heute erleben wir den Triumphder rationellen Technik, stehen jedoch gleichzeitig vor dem Nichts.&Auml;sthetische Leere, uniforme W&uuml;ste, m&ouml;rderischeSterilit&auml;t, sch&ouml;pferische Impotenz.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Den Architekten entgleitetdie Verantwortung ihres Tuns.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Architekten bauenverbrecherisch und menschenunw&uuml;rdig oder zu Beton gewordeneSchnapsideen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sie bauenGef&auml;ngniszellen, in denen die Seele des Menschen zugrundegeht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Architekt handelt wie einKriegsverbrecher. Er befolgt gehorsam Befehle, auch gegen seinGewissen. Er baut Krebsgeschw&uuml;re aus Beton, die die Naturzerfressen und den Menschen mit dazu. Von oben sieht man das ganzgenau.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auch den Malern entgleitetdie Verantwortung ihres Tuns.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der moderne K&uuml;nstler hatsich zu einem M&ouml;chtegern-Diktator entwickelt, v&ouml;lligentfremdet von den Gesetzen der Natur und von dem, was der Menschbraucht und wonach der Mensch sich sehnt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Moderne Kunst ist eineintellektuelle Onanie geworden, erzwungen als kurzlebigesStatussymbol, h&auml;&szlig;lich, kalt, gottlos,herzlos.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die K&uuml;nstler undArchitekten h&auml;tten aber gerade jetzt eine Pflicht zuerf&uuml;llen, als Warnende und Auswegsuchende.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nur wer nach den Gesetzen derNatur und der Vegetation und im Einklang mit dem kosmischenKreislauf lebt, kann nicht fehlgehen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wer sich gegen diese Gesetzestellt, sei es aus &Uuml;berheblichkeit, sei es ausUnterw&uuml;rfigkeit, wird mitverantwortlich am Zusammenbruch undgeht zugrunde.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es geht nicht an, da&szlig;Industrien beispielsweise Kl&auml;ranlagen verhindern wollen, dienur aufgrund von nat&uuml;rlichen T&auml;tigkeiten von Pflanzenfunktionieren. Denn dann k&ouml;nnen sie ihre hochtechnisierten,komplizierten, h&auml;&szlig;lichen und teuren Kl&auml;ranlagennicht verkaufen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Eine nat&uuml;rlicheKl&auml;ranlage ist jedoch sch&ouml;n, wie ein Paradiesgarten, undkostet nichts.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist nur ein Beispiel, wie&Ouml;kologie sch&ouml;pferisch und im Einklang mit der Naturgehandhabt werden mu&szlig;.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sonnenkollektoren,Windm&uuml;hlen, Gasometer, Segelschiffe k&ouml;nnen nurfunktionieren, wenn sie sch&ouml;n sind. Wenn sieh&auml;&szlig;lich sind, funktionieren sie falsch.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die neue Revolution wird vonder Basis her, vom Volk ausgehend den diktatorisch aufgezwungenenpr&auml;fabrizierten Wohnbau hinwegfegen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die neue Revolte wirdsch&ouml;pferisch und sch&ouml;n sein. Die herzloseRationalit&auml;t der Wohnsilos wird von au&szlig;en nach innenzerfressen werden.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Fensterrecht garantiertdas Anrecht des Menschen auf seine dritte Haut, seine Innen- undAu&szlig;enw&auml;nde.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir sind Gast der Natur undm&uuml;ssen die freie Natur auf unsere D&auml;cherzur&uuml;ckgeben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir brauchen &lt;span class=&quot;c2&quot;&gt;Sch&ouml;nheitshindernisse&lt;/span&gt;. DieseSch&ouml;nheitsbarrieren bestehen aus &lt;span class=&quot;c2&quot;&gt;nicht-reglementierten Unregelm&auml;&szlig;igkeiten&lt;/span&gt;.Nur mit ihrer Hilfe k&ouml;nnen wir den Kampf gegen die geradeLinie gewinnen und zu einem natur- und menschengerechten Lebenzur&uuml;ckkehren.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir brauchenSch&ouml;nheitsbarrieren, damit die Welt gr&ouml;&szlig;er wird.Wenn Du und der Nachbar sch&ouml;pferisch t&auml;tig sind, brauchtman nicht weit zu reisen, nicht weit zu gehen, denn das Paradiesist um die Ecke.&lt;/span&gt;&lt;/p&gt;').
text_contents_ge(136,'&lt;style type=&quot;text/css&quot;&gt;
 ol.c1 {margin-top: 0cm}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;Lieber Freund Minich,&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Dank f&uuml;r deinen Brief vom 25. Maibetreffs Fernheizwerk Spittelau. Ich verstehe v&ouml;llig, dass dudagegen bist, ebenso wie ich auch und viele besorgte Menschen. Ichselbst bin in der Brigittenau aufgewachsen, also ganz dicht dabei.Es fehlt in deinem Brief jedoch ein realisierbarerAlternativvorschlag f&uuml;r jetzt. &lt;em&gt;Wir selbst, wir alle, jedereinzelne Wiener, sind f&uuml;r unseren M&uuml;ll verantwortlich.Wenn wir keinen M&uuml;ll produzieren, kann keiner verbranntwerden. Boykottieren wir die M&uuml;llverbrennungsanlagen dadurch,dass wir ihr keinen M&uuml;ll liefern&lt;/em&gt;. Ist das in Wien zurZeit realisierbar? Man m&uuml;sste den M&uuml;llkriminalisieren.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Man m&uuml;sste die M&uuml;llerzeuger, dieVerpackungsindustrie, die M&uuml;llverursacher, dieM&uuml;llmacher, d.h. uns alle empfindlich bestrafen, um eineradikale M&uuml;llvermeidung zu erreichen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Ich habe ein ganzes Jahr mit mir gerungen, mitden gleichen Argumenten wie die deinen f&uuml;r eine Absage, eheich mich entschlossen habe, den Auftrag der architektonischvisuellen Neugestaltung anzunehmen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Wie du wei&szlig;t, bin ich vehement gegenalle Umweltgifte und trete seit immer mit allen meinen Mittelnf&uuml;r eine abfallfreie Gesellschaft ein.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Ich bin jedoch Realist. Man kann nicht dieAugen vor der Realit&auml;t verschlie&szlig;en. Alle meine Utopienund Tr&auml;ume sind realisierbar, was ich auch immer wieder unterBeweis stelle.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Nach eingehender Pr&uuml;fung, was passierenw&uuml;rde, wenn Spittelau nicht in Betrieb geht, d.h. &lt;em&gt;wenn manzu Ende denkt&lt;/em&gt;, sind die negativen Auswirkungen bei weitemschlechter. Denn die abfallfreie Gesellschaft ist nicht f&uuml;rmorgen. Hoffentlich f&uuml;r &uuml;bermorgen. Ich werde vehementdaf&uuml;r k&auml;mpfen und wir alle m&uuml;ssen t&auml;glich daranarbeiten.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die negativen &Uuml;berlegungen:&lt;/p&gt;&lt;ol class=&quot;c1&quot;&gt;&lt;li class=&quot;MsoNormal&quot;&gt;M&uuml;llverbrennung im verbauten Stadtgebietmit der Furcht, dass Giftstoffe innerhalb der Stadt Menschengef&auml;hrden.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;&Uuml;berspringung der Humusetappe durch dieVerbrennung. Der Recycling-Kreis wird unterbrochen.&lt;/li&gt;&lt;/ol&gt;&lt;p class=&quot;MsoEnvelopeReturn&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die positiven &Uuml;berlegungen:&lt;/p&gt;&lt;ol class=&quot;c1&quot;&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Weitgehende, fast vollst&auml;ndigeUnsch&auml;dlichmachung der Giftstoffe im M&uuml;ll durchVerbrennung mit neuer (letzter Stand) De Nox Filteranlage.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Gewinnung von W&auml;rme, 10 - 15 % Wienswird damit geheizt.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Durch meine Neugestaltung wird dem Werk dieH&auml;sslichkeit dieser Gro&szlig;anlage genommen, wodurch diepsychische Belastung durch die immense und bedrohliche optischeUmweltverschmutzung des bisherigen Anblickes wegf&auml;llt.&lt;/li&gt;&lt;/ol&gt;&lt;p class=&quot;MsoEnvelopeReturn&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Ich bin sicher, es entsteht sogar ein Stolz,dieses neue Wahrzeichen Wiens in der N&auml;he zu haben und dieshebt das Selbstbewusstsein und die Lebensfreude eines ganzenStadtteils. Insbesondere wenn auch die Filteranlage und dertechnische Teil weltweit beispielgebend sind.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;So paradox es klingt, ich sehe die SpittelauerAnlage als erstes Mahnmal f&uuml;r eine sch&ouml;nere abfallfreieZukunft. Ich bin auch stolz, dass diese Beispielgebung in&Ouml;sterreich geschieht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Obrigkeit, die B&uuml;rokratie, diePolitiker und verantwortungsvolle Techniker haben getan, was siekonnten. Man muss das anerkennen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Es liegt an uns zu beweisen, dass wir nochmehr und noch besser tun k&ouml;nnen. Proteste allein helfen hiernichts. Nur eine neue Gesellschaft, ausgerichtet nach den wahren&ouml;kologisch-kreativen Werten, die wir selbst schaffen, kanneine schrittweise Wende herbeif&uuml;hren.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Nun im Detail:&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die kalte Verbrennung (Verrottung in Deponienvon M&uuml;ll) hat die gleiche Auswirkung wie die hei&szlig;eVerbrennung.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;ol class=&quot;c1&quot;&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Es wird der gleiche Sauerstoff verbrannt,d.h. der Luft entnommen, nur in einem gr&ouml;&szlig;erenZeitraum.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Die Luft wird mit dem gleichen CO&lt;sub&gt;2&lt;/sub&gt;angereichert in einem gr&ouml;&szlig;eren Zeitraum.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Es entweichen in Luft und Wasser etwa diegleichen Gifte und Schadstoffe, nur dauert es l&auml;nger.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Es entsteht die gleiche W&auml;rme (Energie),allerdings ungen&uuml;tzt. Z.B. ein St&uuml;ck Holz gibt diegleiche Energie ab bei der Humusierung und Mineralisierung in etwa3 - 5 Jahren wie im Ofen verbrannt in nur einer Minute.&lt;/li&gt;&lt;li class=&quot;MsoNormal&quot;&gt;Es entsteht die gleiche Asche(R&uuml;ckstand). Ein St&uuml;ck Holz in einer Minute (hei&szlig;eVerbrennung) oder in 3 - 5 Jahren bei der kalten Verbrennung&lt;/li&gt;&lt;/ol&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Nur: Bei den Deponien werden die Gifte nichtunsch&auml;dlich gemacht und entweichen ungehindert. Insbesonderedie Schwermetalle, die in Spittelau wiedergewonnen werden.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Falls der M&uuml;ll mit Lastwagen aus derStadt gebracht wird, sei es auf eine Deponie, sei es in eineVerbrennungsanlage, sagen wir 30 km von Wien entfernt, w&uuml;rdeallein durch den Rauchgas- und Giftaussto&szlig; derVerbrennungsmotoren der Lastwagen eine weit h&ouml;here undtats&auml;chliche Gef&auml;hrdung der Bev&ouml;lkerung eintretenals durch das Spittelauer Fernheizwerk und zwar zus&auml;tzlich.Man bedenke: etwa 2.000 Tonnen M&uuml;ll t&auml;glich,transportiert mit etwa 300 Lastwagen, mit je Lastwagen etwa 5-malso viel Giftauspuff, ungefiltert, wie bei normalen Autos. Noch dazuauf den Stra&szlig;en in Atemh&ouml;he! Au&szlig;erdem k&ouml;nnteder M&uuml;ll nicht f&uuml;r das Fernw&auml;rmesystem Wiens genutztwerden. Die ohnehin potentiell gef&auml;hrlichen Deponien Wienssind fast voll und k&ouml;nnen nichts mehr aufnehmen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Luftschicht der Erde ist einkommunizierendes System. Ob der Schlot in Wien oder in Alaskasteht, es hat eigentlich die gleiche Auswirkung f&uuml;r alleErdbewohner. So wie die Verschlechterung der Atemluft weltweitkonstatierbar ist, ob in Europa, in der W&uuml;ste Sahara oder inNeuseeland.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Der jetzige unsortierte M&uuml;ll ist derartiggiftig, dass der daraus gewonnene Kompost nicht mehr als Humusf&uuml;r Garten und Landwirtschaft verwendet werden kann.Erschreckenderweise auch nicht f&uuml;r W&auml;lder.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Nach Messungen und Berechnungen wird deranfallende Dioxingehalt, der aus dem Spittelauer Schlotherauskommen wird, pro m&sup3; Luft weniger als 0.0000000004 (9Nullen) betragen. Der tats&auml;chlich eingeatmete Dioxin- bzw.Forangehalt pro m&sup3; Luft wird aber nur maximal 0.00000000000002(13 Nullen) betragen. Ich glaube nicht, dass diese Zahlen erlogensind. Auch weniger Nullen w&auml;ren beeindruckend. DieSchadstoffmenge w&auml;re, proportional auf die Entfernung Mond -Erde (380.000 km) &uuml;bertragen, die Strecke von 1,2Millimetern!&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die zuk&uuml;nftige Filterkapazit&auml;t vonSpittelau ist schon unglaublich. Insbesondere werden auch diegiftigen Schwermetalle Kadmium, Blei, Zink, Chrom etc. zu 99.9 %ausgefiltert und gelangen nicht in Luft oder Grundwasser.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die anfallende Schlacke und Flugasche wird beidem Denox-System ganz besonders unsch&auml;dlich gemacht und quasitotal neutralisiert. Das ist &uuml;berhaupt eine der ganzgro&szlig;en Errungenschaften der neuen Anlage. Au&szlig;erdem istein gro&szlig;er Teil der Schlacke in Glas gebunden.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Aus den Filtern werden die Schwermetallewiedergewonnen. Die Konzentration der Schwermetalle in den Filternist weitaus h&ouml;her als in den Bergwerken und dieR&uuml;ckgewinnung der Schwermetalle ist sehr viel einfacher undenergiesparender. Man gewinnt hohe Mengen von Schwermetallen, dienormalerweise nicht nur verloren gehen, sondern uns allevergiften.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Zigarettenrauch und Autoabgase ergeben eineviel schlimmere Gef&auml;hrdung durch Dioxine, Kohlenmonoxyde,Stickoxyde und so fort. Wie viel Schlote (ungefiltert) dasausmacht, ist nicht auszudenken. Mit einer tats&auml;chlichenGef&auml;hrdung von unten her, in Atemh&ouml;he!&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Wenn man den Auspuff eines einzigen Autoseinatmet ist man binnen Minuten tot. 600.000 Autos sind in Wienregistriert, glaube ich! Trotzdem fahren wir alle Autos, auch dieUmweltsch&uuml;tzer. Wie sind da die Giftzahlen? Auch eine Null mitvielen Nullen?&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Problematik einer Auto-(Abgas-)freienGesellschaft ist &auml;hnlich der einer abfallfreien Gesellschaft.Es geht nicht von heute auf morgen. Bevor ich mich entschlossenhabe, diesen Auftrag anzunehmen, hat mir L&ouml;tsch geholfen,einen Absagebrief zu konzipieren.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Dann war er jedoch auch der Meinung, dass eineVerhinderung der neuen Verbrennungsanlage mangels einerrealistischen jetzigen Alternative &ouml;kologisch einengr&ouml;&szlig;eren Schaden verursachen w&uuml;rde als dieInbetriebnahme und hat die Entscheidung der k&uuml;nstlerischenUmgestaltung mir &uuml;berlassen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Tiefergehende Studien auf globaler Basisbringen das, was ich ohnehin schon wusste. Eine M&uuml;lltrennungbringt nur eine tempor&auml;re Entsch&auml;rfung der&ouml;kologischen Sackgasse. Die kompostierbaren, weitgehendungiftigen Substanzen m&uuml;ssten gesondert der Humusierungzug&auml;nglich gemacht werden.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Jedoch nur eine totale M&uuml;llvermeidung desgesamten Menschengeschlechtes w&auml;re die Antwort: eineabfalllose Gesellschaft.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Und diese totale M&uuml;llvermeidung w&auml;renur die: keine brennbaren Fossilien, keine giftigen undradioaktiven Stoffe aus dem Untergrund der oberen Erdkruste an dieOberfl&auml;che bef&ouml;rdern! Wenn Stoffe wie Erd&ouml;l, Kohleund Giftsubstanzen an die Erdoberfl&auml;che gebracht worden sind,ist es ganz egal, ob man sie verbrennt oder deponiert, imhei&szlig;er oder kalter Verbrennung, ob man sie in Kunststoffe,Plastik etc. umwandelt, ob man sie wiederverwertet, ob man diesePlastik- oder sonstigen Kunststoffprodukte verbrennt oder nichtverbrennt, wiederverwertet oder deponiert. Das Endergebnis ist dasgleiche. Eine mit Giftstoffen angereicherte und aus demGleichgewicht geratene Atmosph&auml;re, ein vergifteter Humus-,Wasser-, Luft-, Pflanzen-, Tier- und Menschenraum.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Also, wenn man die ungeheure Konzentration vonfossilen Brennstoffen, die sich in Jahrmillionen gebildet haben,auf einmal verbrennt oder kalt verbrennt, d.h. deponiert, oder dieProdukte aus &Ouml;l und Kohle verbrennt oder nicht verbrennt, daskatastrophale Resultat ist das gleiche mit ausweglosen Konsequenzenf&uuml;r die n&auml;chsten Generationen. Es gibt nur einen Ausweg.Ab sofort keine fossilen Brennstoffe mehr aus dem Untergrundholen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Natur hat Jahrmillionen gebraucht, um dieGifte, die sich an der Oberfl&auml;che der Erde befanden, mit einerHumus-, Pflanzen- und Sauerstoffschichte zuzudecken. Indem wirdiese Gifte wieder an die Oberfl&auml;che bef&ouml;rdern, erzeugenwir gewaltsam wieder den urspr&uuml;nglichen Zustand von vorJahrmillionen, als der Mensch auf Erden noch nicht lebenkonnte.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Ich hoffe, du siehst die ganze Problematik inihrer Gesamtheit. Man muss f&uuml;r die sofortigen Notwendigkeitennicht nur das kleinere &Uuml;bel, sondern den einzig m&ouml;glichenAusweg zur Zeit w&auml;hlen und t&auml;glich f&uuml;r eine bessereZukunft k&auml;mpfen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Bitte diesen Antwortbrief fairerweise und imInteresse der uns alle betreffenden &ouml;kologischen Sachegleichzeitig mit deinem Brief in den Wiener Naturschutznachrichtenzu ver&ouml;ffentlichen.&lt;/p&gt;').
text_contents_ge(137,'&lt;style type=&quot;text/css&quot;&gt;
 span.c1 {font-family: &quot;Times New Roman&quot;}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Reinigungspsychose isteine Krankheitserscheinung typisch f&uuml;r unsereZivilisation.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Zuerst waren es der Schmutzund die mangelnde Hygiene, die Krankheit und Tod brachten. Jetztist es die &uuml;bertriebene Sterilit&auml;t, die Krankheit und Todverursacht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Alles Organische, chaotischWirkende, die Vielfalt, die unkontrollierte, individuelle Kreationdes Menschen und die Spontanvegetation der Natur gelten alsschmutzig, chaotisch und gef&auml;hrlich.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Uuml;bertriebeneSterilit&auml;t bringt den Tod. Die Waschmittelindustrie wie auchdie Giftspray-Lobby agitieren ungestraft im Fernsehen und in denMedien mit Unwahrheiten, mit einer Gehirn&shy;w&auml;sche, die angewissenloseste politische Propaganda erinnert. Es wird an dieniederen Instinkte einfacher Menschen und Kinder apelliert: DeinHemd mu&szlig; wei&szlig;er sein als das der eifers&uuml;chtigenNachbarin. Da&szlig; dabei t&ouml;dliche Gifte unserenN&auml;chsten und die Natur umbringen, wird verschwiegen. Wirwerden zu Giften und Gegengiften &uuml;berredet. Die Gegengiftewerden immer gef&auml;hrlicher als die Gifte. Der Abfall wird immergef&auml;hrlicher als das Produkt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;In den h&auml;&szlig;lichenund sterilen Satellitenst&auml;dten steht an jedem f&uuml;nftenFenster eine Frau mit einem Waschlappen und putzt ein bereitsperfekt blankes Fenster noch einmal perfekt blank. Das istbedauernswert, aber auch ein gef&auml;hrlicher Wahn.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn an der Wand ein Fleckentsteht, wird gleich die ganze Fassade neu gestrichen, damit jakeine Bereicherung der eint&ouml;nigen, sterilen Fassade eintretenkann.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wehe, wenn ein mit normalerSeife gewaschenes, sauberes Hemd einen Fleck aufweist. Manbeseitigt ihn sofort mit noch gef&auml;hrlicheren Giften, anstattsich &uuml;ber dies unverhoffte Geschenk zu freuen, das geradedieses Hemd aus der anonymen Masse gleicher Hemdenheraushebt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wehe, wenn Gras zwischenTrottoirfugen w&auml;chst, wehe, wenn sich ein Baum spontanentwickelt, der nicht vom Menschen vorgesehen und gepflanztwurde.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das darf nicht sein und wirdbrutal unterdr&uuml;ckt und ausgemerzt. Das ist ein typischesKrankheitssymptom unserer pervers gewordenenZivilisation.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&Uuml;bertriebeneSterilit&auml;t bringt den Tod. Intelligente Gew&auml;hrung vonunkontrolliertem, organischem Wachstum und die Vielfalt bringenLeben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir brauchenSch&ouml;nheitshindernisse. Sch&ouml;nheitshindernisse sindnicht-reglementierte Unregelm&auml;&szlig;igkeiten.&lt;/span&gt;&lt;/p&gt;').
text_contents_ge(138,'&lt;style type=&quot;text/css&quot;&gt;
 span.c1 {font-family: &quot;Times New Roman&quot;}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn der Mensch glaubt, indie Natur korrigieren zu m&uuml;ssen, ist es jedesmal ein nichtwiedergutzumachender Fehler. Es sollte nicht einer Gemeinde zurEhre gereichen, wieviel selbst&shy;gewachsene Natur siezerst&ouml;rt, sondern es sollte vielmehr f&uuml;r eine GemeindeEhrensache sein, soviel wie m&ouml;glich von ihrer nat&uuml;rlichenLandschaft zu sch&uuml;tzen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Bach, der Flu&szlig;, derSumpf, die Aulandschaft in ihrer gottgewollten Beschaffenheitm&uuml;ssen uns heilig und unantastbar sein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Bachregulierungen bringen nurB&ouml;ses, was uns teuer zu stehen kommt:&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Absinken desGrundwassers,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Absinken der Waldgrenze umbis zu 100 m,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Versteppung gro&szlig;erGebiete,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;kein Regenerieren desWassers, das zu schnell abflie&szlig;t.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Aulandschaft kann ihreSchwammt&auml;tigkeit: Aufsaugen von zuviel Wasser und langsamesabgeben bei Trockenheit wie eine gute Sparkasse in Notzeiten -nicht mehr erf&uuml;llen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der regulierte Bach wird zumAbwasserkanal.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Fischsterben und keine Fischeim Bach, weil sie nicht durch den regulierten Kanal k&ouml;nnen.Hochwasser mit verheerenden Folgen erst recht nach der Regulierung,weil zuviel Wasser zu schnell abflie&szlig;t, in gro&szlig;enMengen zusammenkommt, ohne von der Erde und der Vegetationaufgesaugt und abgefangen werden zu k&ouml;nnen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nur ein hoch undunregelm&auml;ssig flie&szlig;ender Bach mit baumbestandenem Uferkann reines Wasser erzeugen, den Wasserhaushalt regulieren und denFisch- und Tierbestand erhalten zum Nutzen des Menschen und seinerLandwirtschaft.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Jetzt, fast zu sp&auml;t,erkennt man diese uralte Weisheit, sprengt einbetonierte,geradlinig gemachte Flu&szlig;- und Bachl&auml;ufe, um denvorherigen unregelm&auml;&szlig;igen Zustand wiederherzustellen.Welche Ironie!&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;&nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Warum also den Bachregulieren, wenn man ihn nachher wieder entregulierenmu&szlig;?&lt;/span&gt;&lt;/p&gt;').
text_contents_ge(139,'&lt;style type=&quot;text/css&quot;&gt;

&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;Copyright bedeutet, da&szlig; etwasGeschaffenes, eine kreative Sch&ouml;pfung, gegen Nachahmung,Ver&auml;nderung, Verst&uuml;mmelung und Zerst&ouml;runggesch&uuml;tzt ist, ob es nun das Zeichen &copy; tr&auml;gt odernicht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;In F&auml;llen, in denen das Copyrightf&uuml;r menschliche Sch&ouml;pfungen verletzt wird, wobei nur derUrheber einen Schaden erleidet und eine begrenzte Anzahl Menschenbetroffen sind, sind die Strafen bereits hart: Sie gelten alskrimineller Tatbestand.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Wenn es um die Verletzung des Copyright derNatursch&ouml;pfung geht, von der die ganze Menschheitabh&auml;ngt, geht es um unendlich mehr, denn dadurch wird allesLebendige auf Erden und die ganze Menschheit bedroht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Der Mensch ist schon seit langem dergef&auml;hrlichste Sch&auml;dling, der je die Erde heimgesuchthat.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Gentechnik ist die bisher kriminellste undhinterh&auml;ltigste Verfehlung des Menschen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Anstatt als Besch&uuml;tzer des Lebensaufzutreten, um die Gesetze und den Reichtum der Erde zu wahren,anstatt sich hinter &ouml;kologische Schranken zur&uuml;ckzuziehen,damit die Erde sich regenerieren kann, spielt der MenschZauberlehrling mit der Kernenergie, mit Hormonen, Giftstoffen, mitAbfallproduktion, mit psychologischer Versklavung, der Bejahung derAbh&auml;ngigkeit, der Versklavung durch die gro&szlig;en Konzerne,der Diktatur der H&auml;&szlig;lichkeit und mit unz&auml;hligenArten der Massenzerst&ouml;rung.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Jetzt ist der Mensch so arrogant geworden, dasInnere der lebendigen Organismen, die in einemzusammenh&auml;ngenden, ausgewogenen, phantastischen System, dasseit Millionen Jahren perfekt funktioniert, miteinander verbundensind, manipulieren zu wollen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die oberste Funktion der Sch&ouml;nheit derganzen Sch&ouml;pfung und ihre gro&szlig;en, erhabenen Gesetze derHarmonie spielen bei der Genmanipulation keineSchl&uuml;sselrolle.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Verachtung der Sch&ouml;nheit, Profitgierund kurzfristige nichtige Vorteile sind die Motivation.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Der Mensch hat nicht das Recht, das Wesen desLebens selbst zu ver&auml;ndern, da er &uuml;ber die langfristigenFolgen, die weit &uuml;ber die Lebensspanne des Menschenhinausgehen, nichts wei&szlig;.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Der Mensch ist dabei, den eigenen Ursprung,die eigene Zukunft, die eigene Existenz zu zerst&ouml;ren, die voneiner Unendlichkeit an weiteren, sorgsam ineinander verwobenengenetischen Gesetzen abh&auml;ngen.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Jedes Lebewesen, jede Lebenserscheinung aufErden &ndash; Schmetterlinge, Blumen, B&auml;ume, Fr&uuml;chte,Schimmelpilze, Tiere, von Mikroben bis zu Elefanten &ndash; hateine &uuml;bergeordnete Funktion, die weit &uuml;ber die jeweiligeN&uuml;tzlichkeit und Nutzbarkeit f&uuml;r den Menschenhinausgeht.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Das entzieht sich g&auml;nzlich demmenschlichen Verstand.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;In seiner Einbildung denkt der Mensch jedochnur an die Ausbeutung f&uuml;r den eigenen fadenscheinigenVorteil.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;F&uuml;r die Genmanipulatoren ist die Natur,die Sch&ouml;pfung, die eigentlich tabu sein sollte, nichts als einwehrloses Opfer, in das man eingreifen und das man nach Beliebenentstellen kann, ohne da&szlig; irgendwelche hinderliche,einschr&auml;nkende Copyright-Bedenken ins Spiel k&auml;men.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;In seiner Habgier vergi&szlig;t der Mensch,da&szlig; er selbst von diesen fein verwobenen Gaben derNatur-Sch&ouml;pfung abh&auml;ngig ist, die aus einem unglaublichphantastischen Schatz herstammen, zu dem der Mensch keinen Zugriffhaben darf.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Wenn der Mensch die Kraft zerst&ouml;rt, dieihn st&uuml;tzt, wenn der Mensch diese Schatzkammer verw&uuml;stet,die sein Leben auf Erden erst erm&ouml;glicht, so zerst&ouml;rt ersich selbst.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&nbsp;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;Die Genmanipulation l&ouml;st den bisherd&uuml;mmsten aller Massenselbstmorde des Menschen aus.&lt;/p&gt;').
text_contents_ge(140,'&lt;style type=&quot;text/css&quot;&gt;
 span.c1 {font-family: Arial}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Lieber Alex Wade,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ich habe eine ziemlich langeErfahrung mit Humustoiletten (7 Jahre) und derPflanzenkl&auml;ranlage (5 Jahre).&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dies sind zwei verschiedeneThemen, die sich aber verbinden lassen. Beide, insbesondere dieHumustoilette, funktionieren gut. Allerdings mit einerEinschr&auml;nkung: Sie funktionieren nur bei mir und ein paarengen, sehr verst&auml;ndnisvollen Freunden. Ich w&uuml;rde nie mitjemandem arbeiten, der die herk&ouml;mmliche &bdquo;Zieh-&ldquo;und &bdquo;Sp&uuml;l-&ldquo; Mentalit&auml;t hat. Ich bezweifle,dass man dem normalen Durchschnittsmenschen, der nicht seitfr&uuml;hester Kindheit dazu erzogen wurde, seine eigeneSchei&szlig;e so wie Humuserde und sauberes Wasser zusch&auml;tzen, zu diesem Zeitpunkt kosteng&uuml;nstigeKompostiertoiletten aufzwingen kann.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich besitze alle B&uuml;cher,die zum Thema Humustoiletten erschienen sind: &bdquo;Stop the FiveGallon Flush&ldquo; &lt;em&gt;(Stoppen Sie die 20-LiterSp&uuml;lung),&lt;/em&gt; &bdquo;Toilette Papers&ldquo;&lt;em&gt;(Toilettenpapiere),&lt;/em&gt; &bdquo;Goodbye to the FlushToilet&ldquo; &lt;em&gt;(Schluss mit Wassersp&uuml;ltoiletten)&lt;/em&gt; sowieStudien und Berichte amerikanischer, schwedischer und norwegischerHersteller, etwa &lt;em&gt;Biocycle (Biozyklus)&lt;/em&gt; etc.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich kaufte von derschwedischen Firma Mullb&auml;nk skandinavische Humustoiletten ausPlastik mit elektrisch betriebenem Abfluss und Abzug,Heizst&auml;ben und einem Trittbrett aus Holz. Ich lie&szlig; siemir nach Neuseeland und Wien schicken. Als sie eintrafen, warenviele Teile zerbrochen. Ich musste Ersatzteile bestellen und vielZoll daf&uuml;r bezahlen. Ich war gar nicht zufrieden.Au&szlig;erdem gefiel mir nicht, dass die Toilette mitElektrizit&auml;t betrieben wurde. Das Summen desBel&uuml;ftungssystems! (Warum zur Natur zur&uuml;ckkehren, wennman dann, wie beim Zahnarzt, st&auml;ndig einem technischenGer&auml;usch ausgeliefert ist?) Mir gefiel auch das Design desAbflusses nicht (mehr wie der eines K&uuml;hlschranks).&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich bin K&uuml;nstler undleide unter allem H&auml;sslichen. H&auml;ssliche Dingefunktionieren nie. Der Clivus ist h&auml;sslich, derh&auml;sslichste Teil ist in der &bdquo;Unterwelt&ldquo; versteckt.Auch das Bel&uuml;ftungssystem, zum Abzug von Ger&uuml;chen undEinstr&ouml;men frischer Luft, war nicht nach meinem Geschmack. Ichging aufs Dach hinauf. Dort stank es. Ich fragte mich, wasw&uuml;rde passieren, wenn zwei Millionen Menschen einer Stadt vonder Gr&ouml;&szlig;e Wiens Kompostiertoiletten mitSchornsteinabz&uuml;gen h&auml;tten. Dann funktionierte er nichtwie im Prospekt beschrieben. Die Toilette trocknete aus, wenn ichabwesend war. Wenn &uuml;berraschend G&auml;ste kamen und dreiWochen blieben, war der Humus zu trocken, aber der Boden mit Urin&uuml;berflutet. Denn die Fl&uuml;ssigkeiten sickerten durch den zutrockenen Humus und aus dem darunter liegenden Auffangbecken, dasden Humus auffangen sollte, lief stinkender Urin.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auch ist mir der Gedankeunsympathisch, dass Humustoiletten teuer sind und man sie, wiejedes andere technische Produkt, kaufen muss.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Aus diesem Grunde begann ichmit selbstgebauten Humustoiletten zu experimentieren, denn ich bingrunds&auml;tzlich von ihrem Nutzen &uuml;berzeugt. Aber siefunktionieren noch nicht perfekt. Zum Beispiel fragte ich denHersteller, wie ich eine Humustoilette auf einem Boot oder einerJacht installieren k&ouml;nnte (ich wei&szlig;, Boote verschmutzenH&auml;fen und Fl&uuml;sse). Ich erhielt keine Antwort.Nat&uuml;rlich nicht. Denn an Bord gibt es nicht genugbatteriegespeicherte Elektrizit&auml;t f&uuml;r den Betrieb einesMull Toa, Mullb&auml;nk, Bio Loo etc. und auch nicht genug Platzf&uuml;r den Clivus, der beinahe gr&ouml;&szlig;er als ein Schiffist.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Also baute ich zuerstkommerzielle Kompostiertoiletten nach, mit Abflussrohr und einerKerosinlampe unter dem Beh&auml;lter, um die Humus-Schei&szlig;e zuerw&auml;rmen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dann fand ich heraus, dass,wenn ich K&uuml;chenabf&auml;lle und Schei&szlig;e mit etwasHumuserde bedecke, weder Geruch entstand noch Fliegen angezogenwurden, ein Abfluss also nicht notwendig war. In diesemZusammenhang las ich in einem der B&uuml;cher, dass dieBel&uuml;ftungssysteme von Kompostiertoiletten bis zu 60% derW&auml;rmeenergie einer Wohnung verbrauchen! Warum bem&uuml;ht mansich, Energie zu sparen und zu speichern, wenn sie durch eineHumustoilette wieder verloren geht! Andererseits erzeugt Humusw&auml;hrend des Zersetzungsprozesses W&auml;rme. Wenn es keinenEntl&uuml;ftungsschornstein gibt, kann die W&auml;rme nichtentweichen, sondern man gewinnt W&auml;rme f&uuml;r den Wohnraum.Ein spezielles Bel&uuml;ftungssystem f&uuml;r die Toilette istnicht erforderlich, die normale Raumbel&uuml;ftung ist ausreichend.Der Toilettenbeh&auml;lter sollte aber von unten bel&uuml;ftetwerden, er sollte L&ouml;cher haben, damit die Luft von unten durchdie Humusmasse geleitet werden und &uuml;bersch&uuml;ssiges Wasserund Urin in ein darunter liegendes Auffangbecken abflie&szlig;enkann.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Urinieren bleibtallerdings ein Problem. Dem interessanten Bericht einesfranz&ouml;sischen Wissenschaftlers entnahm ich, dass alleh&ouml;her entwickelten Tiere, mit Ausnahme der V&ouml;gel,getrennte &Ouml;ffnungen zur Ausscheidung fl&uuml;ssiger und festerVerdauungsr&uuml;ckst&auml;nde haben. Sie haben tats&auml;chlichzwei getrennte Ausscheidungssysteme f&uuml;r fl&uuml;ssige undfeste R&uuml;ckst&auml;nde. Wie wir wissen, ist viel Energieerforderlich, um Fl&uuml;ssiges von Festem zu trennen.M&uuml;llverarbeitungsanlagen leiden unter den enorm hohen Kosten,die bei der Trennung der Fl&uuml;ssigkeiten von Feststoffenentstehen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Trennung von Abfall inFl&uuml;ssigkeiten, Feststoffe, Metalle, Kompost, Glas, Papier,Giftstoffe, Plastik, Fette und so weiter ist extrem teuer undkompliziert. Deshalb stellt sich die Frage: Warum mischen wir das,was die Natur mit gro&szlig;er Weisheit und Sorgfalt getrennt hat?Warum urinieren und schei&szlig;en wir in einen einzigenBeh&auml;lter, wo unser Verdauungssystem vorher beidessorgf&auml;ltig trennt? Diese Trennung muss doch einen Sinnhaben?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch benutzt dieToilette h&auml;ufig, etwa f&uuml;nfmal am Tag: einmal zurAusscheidung von Festem und etwa viermal von Fl&uuml;ssigkeiten.Sowohl M&auml;nner als auch Frauen behaupten, sie k&ouml;nnten beimAusscheiden der Feststoffe die Fl&uuml;ssigkeit nicht einhalten.Das ist zwar fraglich, aber okay. Aber die vier anderen Malebenutzen sie die Toilette nur zum Urinieren!&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Eine Kompostiertoilette ohneHeizungssystem funktioniert nur bei kleinen Urinmengen. Auf derSuche nach einem getrennten System zur Reinigung von Abw&auml;ssernmit hoch entwickelten Pflanzen stie&szlig; ich auf ein System, dasvon Dr. K&auml;the Seidel in Krefeld, Deutschland, entwickeltwurde. Sie hatte Beh&auml;lter, die mit Wasserpflanzen auf einemKiesbett gef&uuml;llt waren, stufenf&ouml;rmig nebeneinanderangeordnet. Extrem schmutziges Abwasser war (nach Durchlaufen dereinzelnen Becken) im letzten Beh&auml;lter kristallklar. Siebepflanzte die Beh&auml;lter mit verschiedenen Sorten vonSchilfgr&auml;sern und Binsen, insbesondere mit Flechtbinse(Schoenoplectus lacustris, fr&uuml;her Scirpus lacustris genannt).Ich verwendete in w&auml;rmerem Klima und in Innenr&auml;umenZypergras (Cyperus, eine artenreiche Sorte, die auch Papyruseinschlie&szlig;t), Winterschachtelhalm (Equisetum hyemale, dassich auch gut f&uuml;r kalte Klimazonen eignet), ganzj&auml;hrigeWasserhyazinthen (z.B. Eichhornia crassipes) in warmem Klima,Sumpfgr&auml;ser, auch schwimmende Wasserrosen und so weiter. Esgibt eine ungeheure Vielfalt von Wasserpflanzen, die Wasserreinigen. Praktisch tun das aber alle Pflanzen, auch solche, diekeine Wasserpflanzen sind.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Pflanzen, die f&uuml;r dieWasserreinigung in Innenr&auml;umen geeignet sind: Zypergras(Cyperus alternifolius und diffusus), Kalla (Calla zantendeschia),Spathiphyllum, Kalmus (Acorus calamus), viele Farne und anderePflanzen, mit denen Sie experimentieren k&ouml;nnen, etwatropischen Sumpfpflanzen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Pflanzen f&uuml;r dieReinigung von Au&szlig;engew&auml;ssern: Alle Schilfe, Binsen,Rohrkolbenschilfe, Sumpfgr&auml;ser, Flechtbinsen (Schoenoplectuslacustris), Wasserrosen, Winterschachtelhalm (Equisetum hyemale),Wasserschwertlilie (Iris pseudacorus), Wasserhyazinthe (z.B.Eichhornia crassipes), Sumpfdotterblumen, Kalla (Calla palustris),Wasserwegerich, Orontium Aquaticum und so weiter. Sie k&ouml;nnenden Wasserstand mit flexiblen Abfl&uuml;ssen oder Rohren mitGelenken regulieren.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Schmutz im Abwasser wirdin Pflanzensubstanz verwandelt. Sie sollten deshalb vieleunterschiedliche Pflanzen verwenden, denn jede Pflanze erf&uuml;llteine andere Reinigungsfunktion. Das gereinigte Wasser im letztenBeh&auml;lter sollte ab und zu noch einmal in dash&ouml;chstgelegene Pflanzengef&auml;&szlig;, welches dasschmutzige Wasser aufnimmt, zur&uuml;ckgegossen werden: jedeStunde, einmal am Tag, einmal pro Woche, ganz wie Sie m&ouml;chtenund wie es Ihren Pflanzen bekommt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Sobald man Wasser in dasoberste Gef&auml;&szlig; gie&szlig;t, wird eine (durch alleBeh&auml;lter flie&szlig;ende) Kaskade in Gang gesetzt, die denberuhigenden Klang eines kleinen Wasserfalls oderBergb&auml;chleins im eigenen Wohnraum verbreitet!&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es ist, als legte man einePlatte auf oder stellte Musik an, als spielte man Bach. &quot;Bach&quot; istauch der Ausdruck f&uuml;r &quot;kleines Fl&uuml;sschen&quot;, &quot;kleinerStrom&quot;. Wie bei einem Plattenspieler hat man f&uuml;r etwa eineStunde Musik.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Wasser sollte vonBeh&auml;lter zu Beh&auml;lter pl&auml;tschern und Sauerstofferzeugen. Das Oberfl&auml;chenwasser jedes Beh&auml;lters sollteauf den Grund des n&auml;chsten Beckens sinken, so dass die Wurzelnder Pflanzen mit Luft in Kontakt kommen. Diese reziproke Aktion vonWasser und Luft ist f&uuml;r die Pflanzenwurzeln von Vorteil, esmacht aus ihnen gesunde, sch&ouml;ne und kr&auml;ftigeReinigungspflanzen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Grundgedanke ist: einenWassergarten mit sch&ouml;nen Pflanzen zu haben, die gleichzeitigdie phantastische Funktion erf&uuml;llen, alleHaushaltabw&auml;sser einschlie&szlig;lich Urin zureinigen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Ich glaube, eineHumustoilette l&auml;sst sich so konstruieren, dass&uuml;bersch&uuml;ssige Fl&uuml;ssigkeit in einePflanzenkl&auml;ranlage abgeleitet wird. Nat&uuml;rlich gilt auchhier: Der Durchschnittsmensch mit seiner Zieh- undSp&uuml;l-Mentalit&auml;t wird mit einer Pflanzenkl&auml;ranlageschlecht umgehen k&ouml;nnen, denn sie erfordert Weisheit, einliebevolles Verst&auml;ndnis f&uuml;r die Bed&uuml;rfnisse derPflanzen und Mikroben, egal ob sie aerobisch oder anaerobischsind.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Und noch ein wichtiger Punkt:F&uuml;r eine sch&ouml;ne Pflanzenkl&auml;ranlage, die gleichzeitigein bezaubernder kleiner Paradiesgarten ist, wird man alles tun,was die Pflanzen gut ern&auml;hrt und sie gesund, sch&ouml;n undlebensf&auml;hig h&auml;lt. Man wird sich h&uuml;ten, Waschmittelund sonstige Gifte in Waschbecken und Badewanne zu geben, weil manwei&szlig;, dass es den Pflanzen schadet. Wer seinen Hund oderseine Katze liebt, gibt ihnen auch nichts Giftiges zu fressen, denner m&ouml;chte sie am Leben erhalten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Bei einerPflanzenkl&auml;ranlage endet die Geschichte nicht beim Abflusslochder Toilette oder des Waschbeckens, denn es bleibt etwas vielSch&ouml;neres: ein neues Leben, ein neuer Anfang, eineVerpflichtung. Man wird versuchen, sein Wasser so sauber zu halten,wie man es bekommen hat, und man wird seinen Abfall in etwasWertvolles verwandeln, n&auml;mlich Humus, und das in Freude undmit Genugtuung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;!--[if gte vml 1]&gt;&lt;v:shape id=&quot;_x0000_s1027&quot; type=&quot;#_x0000_t75&quot;  style=&#039;position:absolute;margin-left:342pt;margin-top:3pt;width:161.8pt;  height:3in;z-index:-1&#039; wrapcoords=&quot;-94 0 -94 21530 21600 21530 21600 0 -94 0&quot;&gt;  &lt;v:imagedata xsrc=&quot;file:///C:DOKUME~1EMPFANGLOKALE~1Tempmsohtml1&lt;/textarea&gt; &lt;div &gt;Please highlight the portion of the text and click on Ctrl-C&lt;/div&gt;&lt;br&gt;    &lt;/td&gt;   &lt;/tr&gt;           &lt;tr valign=&quot;top&quot; id=&quot;fldRow-text_summary&quot; style=&quot;background-color:#eeeeee;color: #000000;&quot;&gt;     &lt;td class=&#039;form-fld-label&#039;&gt;Summary&lt;/td&gt;&lt;td&gt;&lt;!==&lt;a xhref=&#039;copyKeywords()&#039;&gt;Select keyword&lt;/a&gt;--&gt;&lt;/p&gt;').
text_contents_ge(141,'&lt;style type=&quot;text/css&quot;&gt;
 span.c1 {font-size: 12pt}
&lt;/style&gt;
&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Vegetation hatJahrmillionen gebraucht,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;um die Schleimnis, dieGiftstoffe zuzudecken&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;mit einer Humusschicht, einerVegetationsschicht,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;einer Sauerstoffschichte,damit der Mensch auf Erden leben kann.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Und dieser undankbare Menschholt eben diese&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;mit langwieriger kosmischerM&uuml;he zugedeckte Schleimnis&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und eben diese Giftstoffewieder an die Oberfl&auml;che.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;So wird durch die Untat desverantwortungslosen Menschen&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;das Ende der Welt zum Anfangaller Zeiten.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir begehenSelbstmord.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Unsere St&auml;dte sindKrebsgeschw&uuml;re.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Von oben sieht man dasgenau.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir essen nicht das, was beiuns w&auml;chst,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wir holen Essen von weit her,aus Afrika, Amerika, China und Neuseeland.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Schei&szlig;e behaltenwir nicht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Unser Unrat, unser Abfallwird weit weggeschwemmt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir vergiften damitFl&uuml;sse, Seen und Meere,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;oder wir transportierensie&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;in hochkomplizierte teureKl&auml;ranlagen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;selten in zentralisierteKompostierfabriken,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;oder aber unser Abfall wirdvernichtet.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Schei&szlig;e kommt nieauf unsere Felder zur&uuml;ck,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;auch nie dorthin, wo dasEssen herkommt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DER KREISLAUF VOM ESSEN ZURSCHEISSE FUNKTIONIERT.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DER KREISLAUF VON DERSCHEISSE ZUM ESSEN IST UNTERBROCHEN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir machen uns einen falschenBegriff &uuml;ber unseren Abfall.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Jedesmal, wenn wir dieWassersp&uuml;lung bet&auml;tigen, im Glauben,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;eine hygienische Handlung zuvollziehen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;versto&szlig;en wir gegenkosmische Gesetze,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;denn in Wahrheit ist es einegottlose Tat,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;eine frevelhafte Geste desTodes.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wenn wir auf die Toilettegehen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;von innen zusperren undunsere Schei&szlig;e wegsp&uuml;len,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ziehen wir einenSchlu&szlig;strich.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Warum sch&auml;men wiruns?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wovor haben wirAngst?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Was mit unserer Schei&szlig;enachher geschieht,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;verdr&auml;ngen wir wie denTod.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Klosettloch erscheint unswie das Tor in den Tod,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;nur rasch weg davon, nurschnell vergessen die F&auml;ulnis und Verwesung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dabei ist es geradeumgekehrt. Mit der Schei&szlig;e beginnt erst das Leben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DIE SCHEISSE IST VIELWICHTIGER ALS DAS ESSEN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das Essen erh&auml;lt nureine Menschheit,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die sich massenweisevermehrt,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;an Qualit&auml;t sichvermindert und eine Todesgefahr f&uuml;r die Erde gewordenist,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;eine Todesgefahr f&uuml;r dieVegetation, die Tierwelt, das Wasser,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die Luft, dieHumusschicht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Schei&szlig;e aber ist derBaustein unserer Wiederauferstehung.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Seit der Mensch denken kann,versucht er,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;unsterblich zusein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch will seine Seelehaben.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DIE SCHEISSE IST UNSERESEELE.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DURCH DIE SCHEISSEK&Ouml;NNEN WIR &Uuml;BERLEBEN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DURCH DIE SCHEISSE WERDEN WIRUNSTERBLICH.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Warum haben wir Angst vor demTod?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wer eine Humustoiletteben&uuml;tzt,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;hat keine Angst vor demTod,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;denn unsere Schei&szlig;emacht zuk&uuml;nftiges Leben,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;macht unsere Wiedergeburtm&ouml;glich.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WENN WIR UNSERE SCHEISSENICHT SCH&Auml;TZEN&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;UND IN HUMUS UMWANDELN ZUEHREN GOTTES UND DER WELT,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;VERLIEREN WIR UNSEREBERECHTIGUNG,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;AUF DIESER ERDE ANWESEND SEINZU D&Uuml;RFEN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;IM NAMEN FALSCHERHYGIENISCHER GESETZE&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;VERLIEREN WIR UNSEREKOSMISCHE SUBSTANZ,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;VERLIEREN WIR UNSEREWIEDERGEBURT.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;SCHMUTZ IST LEBEN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;STERILE SAUBERKEIT ISTTOD.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Du sollst nichtt&ouml;ten,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;doch wir sterilisieren allesLeben mit Gift und Beton.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist Mord.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mensch ist nur einRohr.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Auf der einen Seite gibt erDinge hinein,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;auf der anderen kommen sieverdaut heraus.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Mund ist vorne, der Afterhinten. Warum?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Umgekehrt sollte essein.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wieso ist speisenpositiv?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wieso ist Schei&szlig;enegativ?&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Was aus uns herauskommt, istkein Abfall,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;sondern der Baustein derWelt,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;unser Gold, unserBlut.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wir verbluten, unsereZivilisation verblutet,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;unsere Erdeverblutet&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;durch die wahnwitzigeUnterbrechung des Kreislaufes.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wer immer zur Aderl&auml;&szlig;t, immer nur Blut verliert&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und nicht durch neuesersetzt, der verblutet.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Freud hatte recht, als er inder Traumdeutung sagte:&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Schei&szlig;e ist synonymf&uuml;r Gold.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Da&szlig; es nicht nur einTraum ist, sondern Wirklichkeit,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;m&uuml;ssen wir jetztfeststellen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Als Pasolini in einem FilmSchauspieler Schei&szlig;e essen lie&szlig;,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;war das ein Symbol desKreislaufschlie&szlig;ens,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ein verzweifeltesBeschleunigen-Wollen.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dieselbe Liebe, dieselbe Zeitund Sorgfalt&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;mu&szlig; aufgewendet werdenf&uuml;r das, was &bdquo;hinten&ldquo; herauskommt,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wie f&uuml;r das, was&bdquo;vorne&ldquo; hineinkommt.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Dieselbe Zeremonie wie beimSpeisen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;mit Tischdecken, Messer,Gabel, L&ouml;ffel,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;chinesischenE&szlig;st&auml;bchen, Silberbesteck und Kerzenlicht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WIR HABEN TISCHGEBETE VOR UNDNACH DEM ESSEN&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;BEIM SCHEISSEN BETETNIEMAND.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WIR DANKEN GOTT F&Uuml;RUNSER T&Auml;GLICH BROT,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DAS AUS DER ERDEKOMMT,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WIR BETEN ABERNICHT,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;AUF DASS SICH UNSERE SCHEISSEWIEDER UMWANDLE.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;ABF&Auml;LLE SINDSCH&Ouml;N.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DAS SORTIEREN UNDWIEDEREINGLIEDERN&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;DER ABF&Auml;LLE IST EINEFROHE T&Auml;TIGKEIT.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Diese T&auml;tigkeit spieltsich nicht in Kellern und Hinterh&ouml;fen,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;auf Mistst&auml;tten,Toiletten und Aborten ab,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;sondern dort, wo wirleben,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wo Licht und Sonneist,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;im Wohnzimmer, in unseremPrunkraum.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Es gibt keineAbf&auml;lle.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Abf&auml;lle existierennicht.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Die Humustoilette ist einStatussymbol.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WIR HABEN DAS PRIVILEG, ZEUGEZU SEIN,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;WIE SICH MIT HILFE UNSERERWEISHEIT&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;UNSER EIGENERABFALL,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;UNSERE EIGENE SCHEISSE INHUMUS UMWANDELT,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;SO WIE DER BAUM W&Auml;CHSTUND DIE ERNTE REIFT.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;BEI UNS ZU HAUSE, ALSW&Auml;R&rsquo;S UNSER EIGENER SOHN.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;HOMO - HUMUS -HUMANITAS,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;drei Schicksalsw&ouml;rtergleichen Ursprungs.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Humus ist das wahre schwarzeGold.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Humus hat einen gutenGeruch.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Humusduft ist heiliger undGott n&auml;her als der Geruch von Weihrauch.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Wer nach dem Regen im Waldspazierengeht, kennt diesen Geruch.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Nat&uuml;rlich ist es etwasUngeheuerliches,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wenn der Abfallk&uuml;bel inden Mittelpunkt unserer Wohnung kommt&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;und die Humustoilette auf demsch&ouml;nsten Platz zum Ehrensitz wird.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Das ist jedoch genau dieKehrtwendung,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;die unsere Gesellschaft,unsere Zivilisation jetzt nehmen mu&szlig;,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;wenn sie &uuml;berlebenwill.&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;Der Humusgeruch ist derGeruch Gottes,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;der Geruch derWiederauferstehung,&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;span class=&quot;c1&quot;&gt;der Geruch derUnsterblichkeit.&lt;/span&gt;&lt;/p&gt;').
